#pragma once


// * The following functions are used to randomly generate an initial solution.
// * Starting from an arbitrarily chosen city, it iteratively selects a random city until forming a complete TSP tour.


int select_random_city(const Config& config, Context& context) {
	int random_start = get_random_int_by_module(config.cities_number);

	for (int i = 0; i < config.cities_number; ++i) {
		int city = (random_start + i) % config.cities_number;

		if (!context.is_city_selected[city]) {
			return city;
		}
	}

	return null;
}

void generate_random_solution(const Config& config, Context& context) {
	// Randomized KNN-chain initialization:
	// start at a random city, then repeatedly pick the next city from the KNN
	// of the current city (choose among the 3 nearest unvisited at random).
	for (int i = 0; i < config.cities_number; ++i) {
		context.solution[i] = null;
		context.is_city_selected[i] = false;
	}

    int current = select_random_city(config, context);
    for (int pos = 0; pos < config.cities_number; ++pos) {
        // place current
        context.solution[pos] = current;
        context.is_city_selected[current] = true;

        // choose next
        int rcl_idx[3];      // indices of restricted candidate list
        double rcl_dist[3];  // distances for ordering
        int rcl_size = 0;

        for (int t = 0; t < config.candidates_number; ++t) {
            int cand = context.candidates[current * config.candidates_number + t];
            if (cand == current || context.is_city_selected[cand]) continue;

            double d = get_distance_double(config, context, current, cand);

            // insert into top-3 by distance
            if (rcl_size < 3) {
                rcl_idx[rcl_size] = cand;
                rcl_dist[rcl_size] = d;
                ++rcl_size;

                // simple insertion sort for size<=3
                for (int k = rcl_size - 1; k > 0; --k) {
                    if (rcl_dist[k] < rcl_dist[k - 1]) {
                        std::swap(rcl_dist[k], rcl_dist[k - 1]);
                        std::swap(rcl_idx[k], rcl_idx[k - 1]);
                    }
                }
            } else if (d < rcl_dist[rcl_size - 1]) {
                rcl_dist[rcl_size - 1] = d;
                rcl_idx[rcl_size - 1] = cand;
                // maintain order
                for (int k = rcl_size - 1; k > 0; --k) {
                    if (rcl_dist[k] < rcl_dist[k - 1]) {
                        std::swap(rcl_dist[k], rcl_dist[k - 1]);
                        std::swap(rcl_idx[k], rcl_idx[k - 1]);
                    }
                }
            }
        }

        int next_city = null;
        if (rcl_size > 0) {
            next_city = rcl_idx[get_random_int_by_module(rcl_size)];
        } else {
            // fallback: pick any unvisited city at random
            next_city = select_random_city(config, context);
        }

        current = next_city == null ? current : next_city;
    }
}
