    }
    if (config.distance_type == DistanceType::Int64) {
        context.path_distance_int64 = calc_total_distance_int64(config, context);
    }
}


void identify_candidates_for_each_node(const Config& config, Context& context, const double* metric, bool is_reversed) {
	for (int i = 0; i < config.cities_number; ++i) {
        std::iota(context.buffer.begin(), context.buffer.end(), 0);  // [0, n)

        auto comp = [&](int u, int v) {
            if (u == i) return false;     // push self to the end
            if (v == i) return true;
            double mu = metric[i * config.cities_number + u];
            double mv = metric[i * config.cities_number + v];
            return is_reversed ? (mu > mv) : (mu < mv);  // heat_map: larger is better; distance: smaller is better
        };

        int take = std::min(config.candidates_number + 1, static_cast<int>(context.buffer.size()));
        std::nth_element(context.buffer.begin(), context.buffer.begin() + take, context.buffer.end(), comp);

        // fill K best excluding self
        int j = 0;
        for (int idx = 0; idx < take && j < config.candidates_number; ++idx) {
            int cand = context.buffer[idx];
            if (cand == i) continue;
            context.candidates[i * config.candidates_number + j++] = cand;
        }
        // backfill if self slipped into top-k
        for (int idx = take; j < config.candidates_number && idx < (int)context.buffer.size(); ++idx) {
            int cand = context.buffer[idx];
            if (cand == i) continue;
            context.candidates[i * config.candidates_number + j++] = cand;
        }
	}
}


int get_random_int_by_module(int mod) {
	return rand() % mod;
}


bool is_cities_same_or_adjacent(const Config& config, Context& context, int i, int j) {
    return (i == j || context.path[i].next == j || context.path[j].next == i);
}


void reverse_sub_path(Context& context, int i, int j) {
    int current_city = i;

    while (true) {
        std::swap(context.path[current_city].prev, context.path[current_city].next);

        if (current_city == j) { return; }

        current_city = context.path[current_city].prev;
    }
}


double expected_tsp_length_2d(long long n, double width, double height) {
    if (n <= 1 || width <= 0.0 || height <= 0.0) {
        return 0.0;
    }

    double area = width * height;
    double expected_length = BHH_CONSTANT_2D * std::sqrt(static_cast<double>(n) * area);
    return expected_length;
}
