#pragma once


// * The following functions are used to generate an initial solution.
// * We provide: Random, Nearest-Neighbor (NN), and Cheapest-Insertion (CI) to seed the local search well.

int select_random_city(const Config& config, Context& context) {
	int random_start = get_random_int_by_module(config.cities_number);

	for (int i = 0; i < config.cities_number; ++i) {
		int city = (random_start + i) % config.cities_number;

		if (!context.is_city_selected[city]) {
			return city;
		}
	}
	return null;
}

void generate_random_solution(const Config& config, Context& context) {
	for (int i = 0; i < config.cities_number; ++i) {
		context.solution[i] = null;
		context.is_city_selected[i] = false;
	}
    for (int i = 0; i < config.cities_number; ++i) {
        int current_city = select_random_city(config, context);
        context.solution[i] = current_city;
        context.is_city_selected[current_city] = true;
    }
}

// Nearest-Neighbor heuristic from a random start
void generate_nearest_neighbor_solution(const Config& config, Context& context) {
    // reset
    for (int i = 0; i < config.cities_number; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    auto dist = [&](int a, int b) -> double {
        if (config.distance_type == DistanceType::Double) return get_distance_double(config, context, a, b);
        if (config.distance_type == DistanceType::Int32)  return static_cast<double>(get_distance_int32(config, context, a, b));
        return static_cast<double>(get_distance_int64(config, context, a, b));
    };

    int current = get_random_int_by_module(config.cities_number);
    for (int pos = 0; pos < config.cities_number; ++pos) {
        context.solution[pos] = current;
        context.is_city_selected[current] = true;

        // pick nearest unselected
        int best = null;
        double best_d = std::numeric_limits<double>::infinity();

        // try KNN first to stay fast
        for (int k = 0; k < config.candidates_number; ++k) {
            int cand = context.candidates[current * config.candidates_number + k];
            if (!context.is_city_selected[cand]) {
                double d = dist(current, cand);
                if (d < best_d) { best_d = d; best = cand; }
            }
        }
        // fallback: scan all if needed
        if (best == null) {
            for (int cand = 0; cand < config.cities_number; ++cand) {
                if (!context.is_city_selected[cand]) {
                    double d = dist(current, cand);
                    if (d < best_d) { best_d = d; best = cand; }
                }
            }
        }
        if (best == null) break;  // done
        current = best;
    }
}

// Cheapest-Insertion heuristic
void generate_cheapest_insertion_solution(const Config& config, Context& context) {
    // reset
    for (int i = 0; i < config.cities_number; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    auto dist = [&](int a, int b) -> double {
        if (config.distance_type == DistanceType::Double) return get_distance_double(config, context, a, b);
        if (config.distance_type == DistanceType::Int32)  return static_cast<double>(get_distance_int32(config, context, a, b));
        return static_cast<double>(get_distance_int64(config, context, a, b));
    };

    // start with two random distinct cities
    int a = get_random_int_by_module(config.cities_number);
    int b = a;
    while (b == a) b = get_random_int_by_module(config.cities_number);

    std::vector<int> tour;
    tour.push_back(a);
    tour.push_back(b);

    context.is_city_selected[a] = true;
    context.is_city_selected[b] = true;

    // insert remaining cities at cheapest position
    for (int inserted = 2; inserted < config.cities_number; ++inserted) {
        int best_city = null;
        int best_pos = -1;
        double best_increase = std::numeric_limits<double>::infinity();

        // choose next city (try candidates from current endpoints, else any)
        // simple: iterate all remaining cities
        for (int city = 0; city < config.cities_number; ++city) {
            if (context.is_city_selected[city]) continue;

            // try inserting between every edge (u -> v)
            int m = static_cast<int>(tour.size());
            for (int idx = 0; idx < m; ++idx) {
                int u = tour[idx];
                int v = tour[(idx + 1) % m];
                double delta = dist(u, city) + dist(city, v) - dist(u, v);
                if (delta < best_increase) {
                    best_increase = delta;
                    best_city = city;
                    best_pos = idx + 1;
                }
            }
        }

        // commit insertion
        tour.insert(tour.begin() + best_pos, best_city);
        context.is_city_selected[best_city] = true;
    }

    // flush to context.solution
    for (int i = 0; i < (int)tour.size(); ++i) {
        context.solution[i] = tour[i];
    }
}
