#pragma once

// Additional heuristics to improve basin exploration & tour quality.

#include <algorithm>
#include <vector>
#include <cstdlib>

// Double-Bridge perturbation (classic 4-opt kick).
// Takes current path in `context.path`, converts to permutation,
// applies 4-cut double-bridge, writes back to path.
inline void apply_double_bridge_perturbation(const Config& config, Context& context, int min_segment = 5) {
    // Make sure we have a valid cycle -> solution
    convert_path_to_solution(config, context);

    const int n = config.cities_number;
    if (n < 12) {  // too small to meaningfully cut 4 segments
        convert_solution_to_path(config, context);
        return;
    }

    // Ensure segments are reasonably sized
    int gap = std::max(min_segment, n / 20);

    int a = 1 + (std::rand() % (n - 4*gap - 1));
    int b = a + gap + (std::rand() % gap);
    int c = b + gap + (std::rand() % gap);
    int d = c + gap + (std::rand() % gap);

    if (d >= n) { d = n - 1; }
    if (!(0 < a && a < b && b < c && c < d && d < n)) {
        convert_solution_to_path(config, context);
        return;
    }

    std::vector<int> &s = *reinterpret_cast<std::vector<int>*>(&context.buffer); // reuse capacity guard
    s.assign(context.solution, context.solution + n);

    std::vector<int> out;
    out.reserve(n);

    // New order: [0..a) + [c..d) + [b..c) + [d..n) + [a..b)
    out.insert(out.end(), s.begin(), s.begin() + a);
    out.insert(out.end(), s.begin() + c, s.begin() + d);
    out.insert(out.end(), s.begin() + b, s.begin() + c);
    out.insert(out.end(), s.begin() + d, s.end());
    out.insert(out.end(), s.begin() + a, s.begin() + b);

    for (int i = 0; i < n; ++i) context.solution[i] = out[i];
    convert_solution_to_path(config, context);
}
