#pragma once

// Extras: helpers & local moves beyond the default set
#include <cmath>
#include "context.hpp"
#include "utils.hpp"

// Check whether b is in the top-K candidate list of a
inline bool is_in_topk(const Config& config, const Context& context, int a, int b) {
    const int K = config.candidates_number;
    const int base = a * K;
    for (int t = 0; t < K; ++t) {
        if (context.candidates[base + t] == b) return true;
    }
    return false;
}

// Mutual-KNN gate
inline bool is_mutual_candidate(const Config& config, const Context& context, int a, int b) {
    return is_in_topk(config, context, a, b) && is_in_topk(config, context, b, a);
}

// Apply a single Or-opt(1) move: detach u and insert between j and j_next.
// Assumes all checks performed by caller.
inline void apply_or1_links(Context& context, int u, int p, int n, int j, int j_next) {
    // remove u
    context.path[p].next = n;
    context.path[n].prev = p;
    // insert u between j and j_next
    context.path[j].next = u;
    context.path[j_next].prev = u;
    context.path[u].prev = j;
    context.path[u].next = j_next;
}

// One pass of Or-opt(1) relocating with first-improvement (fast & effective)
inline int local_or_opt1_search(const Config& config, Context& context) {
    const int n = config.cities_number;
    int improved_times = 0;

    auto getd = [&](int a, int b) -> double {
        if (config.distance_type == DistanceType::Double) {
            return get_distance_double(config, context, a, b);
        } else if (config.distance_type == DistanceType::Int32) {
            return static_cast<double>(get_distance_int32(config, context, a, b));
        } else {
            return static_cast<double>(get_distance_int64(config, context, a, b));
        }
    };

    auto dec_len = [&](double gain) {
        if (config.distance_type == DistanceType::Double) {
            context.path_distance_double -= gain;
        } else if (config.distance_type == DistanceType::Int32) {
            context.path_distance_int32 -= static_cast<int>(gain);
        } else {
            context.path_distance_int64 -= static_cast<long long>(gain);
        }
    };

    while (true) {
        bool improved = false;

        for (int u = 0; u < n; ++u) {
            int p = context.path[u].prev;
            int nn = context.path[u].next;

            // Try inserting u between (j, j_next) for j in top-K of u (mutual-gated)
            const int K = config.candidates_number;
            const int base = u * K;

            for (int k = 0; k < K; ++k) {
                int j = context.candidates[base + k];
                if (j == u || j == p || j == nn) continue;
                if (!is_mutual_candidate(config, context, u, j)) continue;

                int j_next = context.path[j].next;
                if (j_next == u) continue; // would be adjacent

                // Gain (removed - added)
                double removed = getd(p, u) + getd(u, nn) + getd(j, j_next);
                double added   = getd(p, nn) + getd(j, u) + getd(u, j_next);
                double gain = removed - added;

                if (gain > 0.0) {
                    // Weight reinforcement proportional to relative gain
                    double denom = (config.distance_type == DistanceType::Double)
                                   ? context.path_distance_double
                                   : (config.distance_type == DistanceType::Int32)
                                       ? static_cast<double>(context.path_distance_int32)
                                       : static_cast<double>(context.path_distance_int64);
                    double w_inc = config.weight_delta_coefficient * std::expm1(gain / std::max(denom, 1e-12));
                    // Link change
                    apply_or1_links(context, u, p, nn, j, j_next);
                    dec_len(gain);
                    // Update weights for the two added edges
                    update_weight_undirected(config, context, j, u, w_inc);
                    update_weight_undirected(config, context, u, j_next, w_inc);

                    ++improved_times;
                    improved = true;
                    break; // restart scan after a successful move
                }
            }
            if (improved) break;
        }

        if (!improved) break;
    }

    return improved_times;
}
