            double total = 0.0;
            for (int c = 0; c < config.cities_number; ++c) {
                int nxt = context.path[c].next;
                double dx = context.coordinates_double_x[c] - context.coordinates_double_x[nxt];
                double dy = context.coordinates_double_y[c] - context.coordinates_double_y[nxt];
                total += std::sqrt(dx * dx + dy * dy);
            }
            context.path_distance_double = total;
        }
        if (i % 100 == 0) { std::cout << std::setprecision(8) << "Phase #3 (local k'opt search). Total distance: " << context.path_distance_double << ", Improved times: " << improved_times << ", Time: " << duration_cast<milliseconds>(end_time - start_time).count() << " ms\n"; }

