#pragma once


bool apply_2_opt_move(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) { return false; }

    int i_next = context.path[i].next;
    int j_next = context.path[j].next;

    ++context.chosen_times[i * config.cities_number + j];
    ++context.chosen_times[j * config.cities_number + i];
    ++context.chosen_times[i_next * config.cities_number + j_next];
    ++context.chosen_times[j_next * config.cities_number + i_next];

    ++context.total_simulations;

    // Hybrid-precision decision: always decide using double geometry.
    const double d_ii1 = get_distance_double(config, context, i, i_next);
    const double d_jj1 = get_distance_double(config, context, j, j_next);
    const double d_ij  = get_distance_double(config, context, i, j);
    const double d_i1j1= get_distance_double(config, context, i_next, j_next);
    const double delta_d = (d_ii1 + d_jj1) - (d_ij + d_i1j1);

    if (delta_d <= 0.0) return false;

    // Will apply the move; compute weight increase against double tour length for stability.
    double weight_increase = config.weight_delta_coefficient * std::expm1(delta_d / context.path_distance_double);

    // Keep integer path lengths in sync when possible without O(n) recompute.
    if (config.distance_type == DistanceType::Double) {
        context.path_distance_double -= delta_d;
    } else if (config.distance_type == DistanceType::Int32) {
        const int di = get_distance_int32(config, context, i, i_next)
                     + get_distance_int32(config, context, j, j_next)
                     - get_distance_int32(config, context, i, j)
                     - get_distance_int32(config, context, i_next, j_next);
        if (di > 0) context.path_distance_int32 -= di;
        context.path_distance_double -= delta_d;
    } else { // Int64
        const long long dl = get_distance_int64(config, context, i, i_next)
                           + get_distance_int64(config, context, j, j_next)
                           - get_distance_int64(config, context, i, j)
                           - get_distance_int64(config, context, i_next, j_next);
        if (dl > 0) context.path_distance_int64 -= dl;
        context.path_distance_double -= delta_d;
    }

    // applying 2-opt move
    reverse_sub_path(context, i_next, j);

    context.path[i].next = j;
    context.path[i_next].next = j_next;
    context.path[j].prev = i;
    context.path[j_next].prev = i_next;

    // updating weights
    update_weight_undirected(config, context, i, j, weight_increase);
    update_weight_undirected(config, context, i_next, j_next, weight_increase);

    return true;
}


bool improve_by_2_opt_move(const Config& config, Context& context) {
    for (int i = 0; i < config.cities_number; ++i) {
		for (int j = 0; j < config.candidates_number; ++j) {
			int candidate = context.candidates[i * config.candidates_number + j];

			if (apply_2_opt_move(config, context, i, candidate)) { return true; }
		}
	}
    return false;
}

int local_2_opt_search(const Config& config, Context& context) {
    int improved_times = 0;

    while (improve_by_2_opt_move(config, context)) { ++improved_times; };

    return improved_times;
}
