#pragma once


#pragma once

#include <vector>
#include <algorithm>
#include <cstdlib>

// Build a strong initial tour via Nearest Neighbor (random start for diversity).
// Uses precomputed double distances for quality even if runtime uses int distances.
inline void generate_nearest_neighbor_solution(const Config& config, Context& context) {
    const int n = config.cities_number;
    for (int i = 0; i < n; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    int start = rand() % n;
    int current = start;
    context.solution[0] = current;
    context.is_city_selected[current] = true;

    for (int pos = 1; pos < n; ++pos) {
        int best = -1;
        double best_d = inf_double;
        const int base = current * n;

        for (int v = 0; v < n; ++v) {
            if (context.is_city_selected[v] || v == current) continue;
            double d = context.distance_double[base + v];
            if (d < best_d) { best_d = d; best = v; }
        }
        current = best;
        context.solution[pos] = current;
        context.is_city_selected[current] = true;
    }
}

// Classic double-bridge kick to jump to a far basin; then 2-opt will repair.
inline void double_bridge_kick(const Config& config, Context& context) {
    const int n = config.cities_number;
    if (n < 8) return;

    // Make sure solution is in sync
    convert_path_to_solution(config, context);

    int a = 1 + rand() % (n / 4);
    int b = a + 1 + rand() % (n / 4);
    int c = b + 1 + rand() % (n / 4);
    int d = c + 1 + rand() % (n - c - 2);
    if (d >= n - 1) d = n - 2;

    std::vector<int> s(context.solution, context.solution + n);
    std::vector<int> t; t.reserve(n);

    // segments: [0..a], [a+1..b], [b+1..c], [c+1..d], [d+1..n-1]
    t.insert(t.end(), s.begin(), s.begin() + a + 1);
    t.insert(t.end(), s.begin() + c + 1, s.begin() + d + 1);
    t.insert(t.end(), s.begin() + b + 1, s.begin() + c + 1);
    t.insert(t.end(), s.begin() + a + 1, s.begin() + b + 1);
    t.insert(t.end(), s.begin() + d + 1, s.end());

    for (int i = 0; i < n; ++i) context.solution[i] = t[i];
    convert_solution_to_path(config, context);
}
