#pragma once


// This file was specifically created for additional methods, implementations and functions that you can try to improve either time or path length metrics.
=======
#pragma once

// Or-opt(1) relocation pass: cheap O(n·K) improvement using KNN list
// Delta (gain) computed in the active distance metric.
// Gain formula (improvement if gain > 0):
//   gain = d(p,a) + d(a,n) + d(u,un) - [ d(p,n) + d(u,a) + d(a,un) ]

inline bool apply_or_opt_1_move(const Config& config, Context& context, int a, int u) {
    if (a == u) return false;
    int p = context.path[a].prev;
    int n = context.path[a].next;
    if (p == a || n == a) return false;
    if (u == p || u == a || u == n) return false;  // skip adjacent/self
    int un = context.path[u].next;
    if (un == a) return false; // inserting after u where u->a already exists changes nothing

    auto gain_double = [&]() {
        return get_distance_double(config, context, p, a)
             + get_distance_double(config, context, a, n)
             + get_distance_double(config, context, u, un)
             - ( get_distance_double(config, context, p, n)
               + get_distance_double(config, context, u, a)
               + get_distance_double(config, context, a, un) );
    };
    auto gain_int32 = [&]() {
        return get_distance_int32(config, context, p, a)
             + get_distance_int32(config, context, a, n)
             + get_distance_int32(config, context, u, un)
             - ( get_distance_int32(config, context, p, n)
               + get_distance_int32(config, context, u, a)
               + get_distance_int32(config, context, a, un) );
    };
    auto gain_int64 = [&]() {
        return get_distance_int64(config, context, p, a)
             + get_distance_int64(config, context, a, n)
             + get_distance_int64(config, context, u, un)
             - ( get_distance_int64(config, context, p, n)
               + get_distance_int64(config, context, u, a)
               + get_distance_int64(config, context, a, un) );
    };

    bool improved = false;
    if (config.distance_type == DistanceType::Double) {
        double g = gain_double();
        if (g > 0.0) {
            context.path_distance_double -= g;
            improved = true;
        }
    } else if (config.distance_type == DistanceType::Int32) {
        int g = gain_int32();
        if (g > 0) {
            context.path_distance_int32 -= g;
            improved = true;
        }
    } else { // Int64
        long long g = gain_int64();
        if (g > 0) {
            context.path_distance_int64 -= g;
            improved = true;
        }
    }

    if (!improved) return false;

    // Relink: remove a from (p,a,n), insert after u
    // detach a
    context.path[p].next = n;
    context.path[n].prev = p;

    // insert a after u
    int un_after = un;
    context.path[u].next = a;
    context.path[a].prev = u;
    context.path[a].next = un_after;
    context.path[un_after].prev = a;

    return true;
}

// Returns number of successful relocations
inline int local_or_opt_search(const Config& config, Context& context) {
    int improved_times = 0;
    bool improved = true;
    while (improved) {
        improved = false;
        for (int a = 0; a < config.cities_number; ++a) {
            // try inserting a after its KNN candidates
            for (int j = 0; j < config.candidates_number; ++j) {
                int u = context.candidates[a * config.candidates_number + j];
                if (apply_or_opt_1_move(config, context, a, u)) {
                    ++improved_times;
                    improved = true;
                }
            }
        }
    }
    return improved_times;
}
