#pragma once


bool apply_2_opt_move(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) { return false; }

    const int i_next = context.path[i].next;
    const int j_next = context.path[j].next;

    // bandit statistics
    ++context.chosen_times[i * config.cities_number + j];
    ++context.chosen_times[j * config.cities_number + i];
    ++context.chosen_times[i_next * config.cities_number + j_next];
    ++context.chosen_times[j_next * config.cities_number + i_next];
    ++context.total_simulations;

    // --- Use DOUBLE geometry for acceptance (quality-first), keep integer bookkeeping for speed ---
    const double d_ii1 = get_distance_double(config, context, i, i_next);
    const double d_jj1 = get_distance_double(config, context, j, j_next);
    const double d_ij   = get_distance_double(config, context, i, j);
    const double d_i1j1 = get_distance_double(config, context, i_next, j_next);
    const double delta_d = (d_ii1 + d_jj1) - (d_ij + d_i1j1);

    if (delta_d <= 0.0) {
        return false;
    }

    // weight update computed against CURRENT tour length (double)
    const double Ld = context.path_distance_double;
    const double weight_increase = config.weight_delta_coefficient * std::expm1(delta_d / Ld);

    // Apply the 2-opt reversal on the path structure
    reverse_sub_path(context, i_next, j);
    context.path[i].next = j;
    context.path[i_next].next = j_next;
    context.path[j].prev = i;
    context.path[j_next].prev = i_next;

    // Update stored tour lengths
    context.path_distance_double -= delta_d;

    if (config.distance_type == DistanceType::Int32) {
        const int dii1 = get_distance_int32(config, context, i, i_next);
        const int djj1 = get_distance_int32(config, context, j, j_next);
        const int dij  = get_distance_int32(config, context, i, j);
        const int di1j1= get_distance_int32(config, context, i_next, j_next);
        const int delta_i = (dii1 + djj1) - (dij + di1j1);
        // integer delta can be <=0 due to rounding; we still update bookkeeping to stay consistent
        context.path_distance_int32 -= delta_i;
    } else if (config.distance_type == DistanceType::Int64) {
        const long long dii1 = get_distance_int64(config, context, i, i_next);
        const long long djj1 = get_distance_int64(config, context, j, j_next);
        const long long dij  = get_distance_int64(config, context, i, j);
        const long long di1j1= get_distance_int64(config, context, i_next, j_next);
        const long long delta_l = (dii1 + djj1) - (dij + di1j1);
        context.path_distance_int64 -= delta_l;
    }

    // Update weights on improved edges
    update_weight_undirected(config, context, i, j, weight_increase);
    update_weight_undirected(config, context, i_next, j_next, weight_increase);

    return true;
}


bool improve_by_2_opt_move(const Config& config, Context& context) {
    const int n = config.cities_number;
    for (int i = 0; i < n; ++i) {
        const int i_next = context.path[i].next;

        // 1) Try neighbors of i
		for (int j = 0; j < config.candidates_number; ++j) {
			int cand = context.candidates[i * config.candidates_number + j];
			if (apply_2_opt_move(config, context, i, cand)) { return true; }
		}

        // 2) Try neighbors of i_next (union-neighbors heuristic)
        for (int j = 0; j < config.candidates_number; ++j) {
            int cand = context.candidates[i_next * config.candidates_number + j];
            if (apply_2_opt_move(config, context, i, cand)) { return true; }
        }

        // 3) Light random probe to escape candidate bias
        for (int t = 0; t < 64; ++t) {
            int jcity = get_random_int_by_module(n);
            if (apply_2_opt_move(config, context, i, jcity)) { return true; }
        }
	}
    return false;
}

int local_2_opt_search(const Config& config, Context& context) {
    int improved_times = 0;

    while (improve_by_2_opt_move(config, context)) { ++improved_times; };

    return improved_times;
}
