#pragma once

#include <vector>
#include <algorithm>
#include <cstdlib>

// Build a strong initial tour via Nearest Neighbor (random start for diversity).
// Uses precomputed double distances for quality even if runtime uses int distances.
inline void generate_nearest_neighbor_solution(const Config& config, Context& context) {
    const int n = config.cities_number;
    for (int i = 0; i < n; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    int start = rand() % n;
    int current = start;
    context.solution[0] = current;
    context.is_city_selected[current] = true;

    for (int pos = 1; pos < n; ++pos) {
        int best = -1;
        double best_d = inf_double;
        const int base = current * n;

        for (int v = 0; v < n; ++v) {
            if (context.is_city_selected[v] || v == current) continue;
            double d = context.distance_double[base + v];
            if (d < best_d) { best_d = d; best = v; }
        }
        current = best;
        context.solution[pos] = current;
        context.is_city_selected[current] = true;
    }
}

// Cheapest-Insertion initializer (quality-first seed):
// 1) pick farthest pair (a,b);
// 2) insert c that minimizes Δ = d(a,c)+d(c,b)-d(a,b);
// 3) iterate: insert the city v and position (u→w) with minimal Δ(v; u→w).
inline void generate_cheapest_insertion_solution(const Config& config, Context& context) {
    const int n = config.cities_number;

    // reset
    for (int i = 0; i < n; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    // farthest pair (a,b)
    int a = 0, b = 1;
    double best_far = -1.0;
    for (int i = 0; i < n; ++i) {
        const int base = i * n;
        for (int j = i + 1; j < n; ++j) {
            double d = context.distance_double[base + j];
            if (d > best_far) { best_far = d; a = i; b = j; }
        }
    }

    std::vector<int> tour;
    tour.reserve(n);
    tour.push_back(a);
    tour.push_back(b);

    context.is_city_selected[a] = true;
    context.is_city_selected[b] = true;

    // best 3rd city by cheapest insertion into edge (a->b)
    int c_best = -1;
    double c_cost = inf_double;
    const double dab = context.distance_double[a * n + b];
    for (int v = 0; v < n; ++v) {
        if (context.is_city_selected[v]) continue;
        double cost = context.distance_double[a * n + v] + context.distance_double[v * n + b] - dab;
        if (cost < c_cost) { c_cost = cost; c_best = v; }
    }
    tour.insert(tour.begin() + 1, c_best);
    context.is_city_selected[c_best] = true;

    // insert remaining
    while ((int)tour.size() < n) {
        int best_city = -1, best_pos = -1;
        double best_delta = inf_double;

        for (int v = 0; v < n; ++v) {
            if (context.is_city_selected[v]) continue;

            for (int t = 0; t < (int)tour.size(); ++t) {
                int u = tour[t];
                int w = tour[(t + 1) % tour.size()];
                double delta = context.distance_double[u * n + v]
                             + context.distance_double[v * n + w]
                             - context.distance_double[u * n + w];
                if (delta < best_delta) {
                    best_delta = delta;
                    best_city = v;
                    best_pos  = t + 1;
                }
            }
        }

        tour.insert(tour.begin() + best_pos, best_city);
        context.is_city_selected[best_city] = true;
    }

    // emit into context.solution
    for (int i = 0; i < n; ++i) context.solution[i] = tour[i];
}

// Classic double-bridge kick to jump to a far basin; then 2-opt will repair.
inline void double_bridge_kick(const Config& config, Context& context) {
    const int n = config.cities_number;
    if (n < 8) return;

    // Make sure solution is in sync
    convert_path_to_solution(config, context);

    int a = 1 + rand() % (n / 4);
    int b = a + 1 + rand() % (n / 4);
    int c = b + 1 + rand() % (n / 4);
    int d = c + 1 + rand() % (n - c - 2);
    if (d >= n - 1) d = n - 2;

    std::vector<int> s(context.solution, context.solution + n);
    std::vector<int> t; t.reserve(n);

    // segments: [0..a], [a+1..b], [b+1..c], [c+1..d], [d+1..n-1]
    t.insert(t.end(), s.begin(), s.begin() + a + 1);
    t.insert(t.end(), s.begin() + c + 1, s.begin() + d + 1);
    t.insert(t.end(), s.begin() + b + 1, s.begin() + c + 1);
    t.insert(t.end(), s.begin() + a + 1, s.begin() + b + 1);
    t.insert(t.end(), s.begin() + d + 1, s.end());

    for (int i = 0; i < n; ++i) context.solution[i] = t[i];
    convert_solution_to_path(config, context);
}
