void identify_candidates_for_each_node(const Config& config, Context& context, const double* metric, bool is_reversed) {
	for (int i = 0; i < config.cities_number; ++i) {
        std::iota(context.buffer.begin(), context.buffer.end(), 0);  // 0..n-1

        // Partition so top (k+1) are the closest (self may be among them); comparator keeps 'i' away from the front.
        std::nth_element(
            context.buffer.begin(),
            context.buffer.begin() + std::min(config.candidates_number + 1, static_cast<int>(context.buffer.size())),
            context.buffer.end(),
            [&](int u, int v) {
                if (i == u) { return false; }
                if (i == v) { return true; }
                return static_cast<bool>((metric[i * config.cities_number + u] < metric[i * config.cities_number + v]) ^ is_reversed);
            }
        );

        // Fill K slots, explicitly skipping self and backfilling from the partition tail if needed.
        int filled = 0;
        for (size_t idx = 0; idx < context.buffer.size() && filled < config.candidates_number; ++idx) {
            int cand = context.buffer[idx];
            if (cand == i) { continue; }
            context.candidates[i * config.candidates_number + filled++] = cand;
        }
	}
