#pragma once

// Steepest 2-opt + Or-opt(1) with true-Euclidean acceptance (even when searching with ints)

inline double two_opt_gain_true(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) return 0.0;

    int i_next = context.path[i].next;
    int j_next = context.path[j].next;

    double old_len = calc_distance_double(context, i, i_next) + calc_distance_double(context, j, j_next);
    double new_len = calc_distance_double(context, i, j) + calc_distance_double(context, i_next, j_next);
    return old_len - new_len;
}

bool apply_2_opt_move(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) { return false; }

    int i_next = context.path[i].next;
    int j_next = context.path[j].next;

    ++context.chosen_times[i * config.cities_number + j];
    ++context.chosen_times[j * config.cities_number + i];
    ++context.chosen_times[i_next * config.cities_number + j_next];
    ++context.chosen_times[j_next * config.cities_number + i_next];
    ++context.total_simulations;

    // Accept by true Euclidean gain
    double delta_true = two_opt_gain_true(config, context, i, j);
    if (delta_true <= 1e-12) return false;

    double weight_increase = config.weight_delta_coefficient * std::expm1(delta_true / std::max(1e-12, context.path_distance_double));

    // apply reversal
    reverse_sub_path(context, i_next, j);
    context.path[i].next = j;
    context.path[i_next].next = j_next;
    context.path[j].prev = i;
    context.path[j_next].prev = i_next;

    // update tracked lengths
    context.path_distance_double -= delta_true;
    if (config.distance_type == DistanceType::Int32) {
        int delta_i = get_distance_int32(config, context, i, i_next) + get_distance_int32(config, context, j, j_next)
                    - get_distance_int32(config, context, i, j) - get_distance_int32(config, context, i_next, j_next);
        if (delta_i > 0) context.path_distance_int32 -= delta_i;
    }
    if (config.distance_type == DistanceType::Int64) {
        long long delta_l = get_distance_int64(config, context, i, i_next) + get_distance_int64(config, context, j, j_next)
                          - get_distance_int64(config, context, i, j) - get_distance_int64(config, context, i_next, j_next);
        if (delta_l > 0) context.path_distance_int64 -= delta_l;
    }

    // weights for added edges
    update_weight_undirected(config, context, i, j, weight_increase);
    update_weight_undirected(config, context, i_next, j_next, weight_increase);
    return true;
}

bool improve_by_2_opt_move(const Config& config, Context& context) {
    double best_gain = 0.0;
    int bi = -1, bj = -1;

    for (int i = 0; i < config.cities_number; ++i) {
        for (int t = 0; t < config.candidates_number; ++t) {
            int j = context.candidates[i * config.candidates_number + t];
            if (is_cities_same_or_adjacent(config, context, i, j)) continue;
            double g = two_opt_gain_true(config, context, i, j);
            if (g > best_gain) { best_gain = g; bi = i; bj = j; }
        }
    }
    if (best_gain > 1e-12) {
        return apply_2_opt_move(config, context, bi, bj);
    }
    return false;
}

// --- Or-opt(1): relocate single node u ---
inline double or1_gain_true(const Config& config, Context& context, int u, int j) {
    int p = context.path[u].prev;
    int n = context.path[u].next;
    if (p == j || n == j) return 0.0;
    int jn = context.path[j].next;
    if (jn == u) return 0.0;

    double old_len = calc_distance_double(context, p, u) + calc_distance_double(context, u, n) + calc_distance_double(context, j, jn);
    double new_len = calc_distance_double(context, p, n) + calc_distance_double(context, j, u) + calc_distance_double(context, u, jn);
    return old_len - new_len;
}

bool apply_or_opt1_move(const Config& config, Context& context, int u, int j) {
    int p = context.path[u].prev;
    int n = context.path[u].next;
    int jn = context.path[j].next;

    double delta_true = or1_gain_true(config, context, u, j);
    if (delta_true <= 1e-12) return false;

    double weight_increase = config.weight_delta_coefficient * std::expm1(delta_true / std::max(1e-12, context.path_distance_double));

    // detach u
    context.path[p].next = n;
    context.path[n].prev = p;

    // insert u between j and jn
    context.path[j].next = u;
    context.path[u].prev = j;
    context.path[u].next = jn;
    context.path[jn].prev = u;

    // update lengths
    context.path_distance_double -= delta_true;
    if (config.distance_type == DistanceType::Int32) {
        int old_i = get_distance_int32(config, context, p, u) + get_distance_int32(config, context, u, n) + get_distance_int32(config, context, j, jn);
        int new_i = get_distance_int32(config, context, p, n) + get_distance_int32(config, context, j, u) + get_distance_int32(config, context, u, jn);
        int di = old_i - new_i;
        if (di > 0) context.path_distance_int32 -= di;
    }
    if (config.distance_type == DistanceType::Int64) {
        long long old_l = get_distance_int64(config, context, p, u) + get_distance_int64(config, context, u, n) + get_distance_int64(config, context, j, jn);
        long long new_l = get_distance_int64(config, context, p, n) + get_distance_int64(config, context, j, u) + get_distance_int64(config, context, u, jn);
        long long dl = old_l - new_l;
        if (dl > 0) context.path_distance_int64 -= dl;
    }

    // reinforce added edges
    update_weight_undirected(config, context, p, n, weight_increase);
    update_weight_undirected(config, context, j, u, weight_increase);
    update_weight_undirected(config, context, u, jn, weight_increase);

    return true;
}

bool improve_by_or_opt1_move(const Config& config, Context& context) {
    double best_gain = 0.0;
    int bu = -1, bj = -1;

    for (int u = 0; u < config.cities_number; ++u) {
        for (int t = 0; t < config.candidates_number; ++t) {
            int j = context.candidates[u * config.candidates_number + t];
            double g = or1_gain_true(config, context, u, j);
            if (g > best_gain) { best_gain = g; bu = u; bj = j; }
        }
    }
    if (best_gain > 1e-12) {
        return apply_or_opt1_move(config, context, bu, bj);
    }
    return false;
}

int local_2_opt_search(const Config& config, Context& context) {
    int improved_times = 0;
    while (true) {
        bool improved_2opt = false;
        while (improve_by_2_opt_move(config, context)) { ++improved_times; improved_2opt = true; }
        if (improved_2opt) continue;

        if (improve_by_or_opt1_move(config, context)) { ++improved_times; continue; }
        break; // no 2-opt, no Or-opt(1)
    }
    return improved_times;
}
