        }

        if (apply_move) { break; }

        current_city = proposed_city_link;
    }

    if (apply_move) {
        // concluding the path to cycle
        int end_city = context.pairs[2 * depth - 1];

        context.path[start_city].next = end_city;
        context.path[end_city].prev = start_city;

        return true;
    }
    return false;
}


