// Greedy nearest-neighbor seeding using the KNN metric matrix (distance_double):
// - For DistanceType::Double it contains true Euclidean distances;
// - For Int32/Int64 we stored squared distances, which preserves ordering.
void generate_greedy_nn_solution(const Config& config, Context& context) {
    const int n = config.cities_number;
    for (int i = 0; i < n; ++i) { context.is_city_selected[i] = false; }

    int start = get_random_int_by_module(n);
    context.solution[0] = start;
    context.is_city_selected[start] = true;

    for (int t = 1; t < n; ++t) {
        int prev = context.solution[t - 1];
        int best = -1;
        double best_metric = inf_double;
        for (int j = 0; j < n; ++j) {
            if (context.is_city_selected[j] || j == prev) continue;
            double m = context.distance_double[prev * n + j];
            if (m < best_metric) { best_metric = m; best = j; }
        }
        if (best == -1) { // fallback
            best = select_random_city(config, context);
        }
        context.solution[t] = best;
        context.is_city_selected[best] = true;
    }
}
