#pragma once


// * The following functions are used to randomly generate an initial solution.
// * Starting from an arbitrarily chosen city, it iteratively selects a random city until forming a complete TSP tour.


int select_random_city(const Config& config, Context& context) {
	int random_start = get_random_int_by_module(config.cities_number);

	for (int i = 0; i < config.cities_number; ++i) {
		int city = (random_start + i) % config.cities_number;

		if (!context.is_city_selected[city]) {
			return city;
		}
	}

	return null;
}

void generate_random_solution(const Config& config, Context& context) {
	for (int i = 0; i < config.cities_number; ++i) {
		context.solution[i] = null;
		context.is_city_selected[i] = false;
	}

    for (int i = 0; i < config.cities_number; ++i) {
        int current_city = select_random_city(config, context);
        context.solution[i] = current_city;
        context.is_city_selected[current_city] = true;
    }
}

/**
 * Greedy nearest-neighbor initializer over candidate lists.
 * Uses context.distance_double as metric:
 *  - DistanceType::Double: true Euclidean
 *  - Otherwise: squared Euclidean (ordering preserved; sqrt omitted)
 *
 * Complexity: O(n * candidates_number) with rare O(n) fallbacks.
 * Produces a much shorter seed tour than uniform random.
 */
void generate_greedy_nn_solution(const Config& config, Context& context) {
    // reset
    for (int i = 0; i < config.cities_number; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    const double* metric = context.distance_double;
    const int n = config.cities_number;
    const int k = config.candidates_number;

    int start = select_random_city(config, context);
    int current = start;

    for (int t = 0; t < n; ++t) {
        context.solution[t] = current;
        context.is_city_selected[current] = true;

        // choose nearest unselected candidate
        int best_next = null;
        double best_cost = inf_double;

        // scan candidate list
        for (int j = 0; j < k; ++j) {
            int cand = context.candidates[current * k + j];
            if (cand == current || context.is_city_selected[cand]) { continue; }
            double c = metric[current * n + cand];
            if (c < best_cost) { best_cost = c; best_next = cand; }
        }

        // rare fallback: full scan if all candidates are already taken
        if (best_next == null) {
            for (int cand = 0; cand < n; ++cand) {
                if (cand == current || context.is_city_selected[cand]) { continue; }
                double c = metric[current * n + cand];
                if (c < best_cost) { best_cost = c; best_next = cand; }
            }
        }

        if (best_next == null) {
            // only happens at the last step
            // try to pick any remaining city
            for (int cand = 0; cand < n; ++cand) {
                if (!context.is_city_selected[cand]) { best_next = cand; break; }
            }
        }

        current = (best_next == null) ? start : best_next;
    }
}
