#pragma once

// Or-opt (1-node relocation) local search, KNN-constrained.
// Moves a single node 'a' from between (u,a,v) to after u_ins (between u_ins and u_next)
// if it shortens the tour. Uses the active distance type and updates path length + weights.

inline bool apply_or_opt_1(const Config& config, Context& context, int a, int u_ins) {
    if (a == u_ins) return false;

    const int u = context.path[a].prev;
    const int v = context.path[a].next;
    if (u == a || v == a) return false;

    // Do not insert adjacent to itself; also avoid no-op positions
    if (u_ins == u || u_ins == v || u_ins == a) return false;

    const int u_next = context.path[u_ins].next;
    if (u_next == a || u_next == u || u_next == u_ins) return false;

    bool improved = false;
    double w_increase = 0.0;

    if (config.distance_type == DistanceType::Double) {
        const double old_sum =
            get_distance_double(config, context, u, a) +
            get_distance_double(config, context, a, v) +
            get_distance_double(config, context, u_ins, u_next);

        const double new_sum =
            get_distance_double(config, context, u, v) +
            get_distance_double(config, context, u_ins, a) +
            get_distance_double(config, context, a, u_next);

        const double delta = old_sum - new_sum;
        if (delta > 0.0) {
            context.path_distance_double -= delta;
            w_increase = config.weight_delta_coefficient * (std::exp(delta / (context.path_distance_double + new_sum)) - 1.0);
            improved = true;
        }
    } else if (config.distance_type == DistanceType::Int32) {
        const int old_sum =
            get_distance_int32(config, context, u, a) +
            get_distance_int32(config, context, a, v) +
            get_distance_int32(config, context, u_ins, u_next);

        const int new_sum =
            get_distance_int32(config, context, u, v) +
            get_distance_int32(config, context, u_ins, a) +
            get_distance_int32(config, context, a, u_next);

        const int delta = old_sum - new_sum;
        if (delta > 0) {
            context.path_distance_int32 -= delta;
            w_increase = config.weight_delta_coefficient * (std::exp(static_cast<double>(delta) / (context.path_distance_int32 + new_sum)) - 1.0);
            improved = true;
        }
    } else { // Int64
        const long long old_sum =
            get_distance_int64(config, context, u, a) +
            get_distance_int64(config, context, a, v) +
            get_distance_int64(config, context, u_ins, u_next);

        const long long new_sum =
            get_distance_int64(config, context, u, v) +
            get_distance_int64(config, context, u_ins, a) +
            get_distance_int64(config, context, a, u_next);

        const long long delta = old_sum - new_sum;
        if (delta > 0) {
            context.path_distance_int64 -= delta;
            w_increase = config.weight_delta_coefficient * (std::exp(static_cast<double>(delta) / (context.path_distance_int64 + new_sum)) - 1.0);
            improved = true;
        }
    }

    if (!improved) return false;

    // Detach a from (u,a,v)
    context.path[u].next = v;
    context.path[v].prev = u;

    // Insert a after u_ins (between u_ins and u_next)
    context.path[a].prev = u_ins;
    context.path[a].next = u_next;
    context.path[u_ins].next = a;
    context.path[u_next].prev = a;

    // Weight updates on newly created edges
    update_weight_undirected(config, context, u, v, w_increase);
    update_weight_undirected(config, context, u_ins, a, w_increase);
    update_weight_undirected(config, context, a, u_next, w_increase);

    return true;
}

inline bool improve_by_or_opt_1(const Config& config, Context& context) {
    // Try relocating each city using its candidate set as insertion anchors.
    for (int a = 0; a < config.cities_number; ++a) {
        for (int j = 0; j < config.candidates_number; ++j) {
            int u_ins = context.candidates[a * config.candidates_number + j];
            if (apply_or_opt_1(config, context, a, u_ins)) {
                return true; // apply first improving move (first-improvement)
            }
        }
    }
    return false;
}

inline int local_or_opt_search(const Config& config, Context& context) {
    int improved_times = 0;
    while (improve_by_or_opt_1(config, context)) { ++improved_times; }
    return improved_times;
}
