// Or-opt(1) relocate: move single node u from (p,u,n) to between (j,j_next)
// Gain (true Euclidean):
//   Δ = [d(p,u)+d(u,n)+d(j,j_next)] - [d(p,n)+d(j,u)+d(u,j_next)]
// ------------------------------------------------------------
static inline bool apply_or_opt1_move(const Config& config, Context& context, int u, int j) {
    int p = context.path[u].prev;
    int n = context.path[u].next;
    if (p == j || n == j || u == j) return false;
    int j_next = context.path[j].next;
    if (j_next == u || j_next == p) return false;

    const double gain_true =
        calc_distance_double(context, p, u) + calc_distance_double(context, u, n) + calc_distance_double(context, j, j_next)
        - (calc_distance_double(context, p, n) + calc_distance_double(context, j, u) + calc_distance_double(context, u, j_next));

    if (gain_true <= 0.0) return false;

    // detach u
    context.path[p].next = n;
    context.path[n].prev = p;

    // insert between j and j_next
    context.path[j].next = u;
    context.path[u].prev = j;
    context.path[u].next = j_next;
    context.path[j_next].prev = u;

    context.path_distance_double -= gain_true;

    if (config.distance_type == DistanceType::Int32) {
        int gain_i =
            get_distance_int32(config, context, p, u) + get_distance_int32(config, context, u, n) + get_distance_int32(config, context, j, j_next)
            - (get_distance_int32(config, context, p, n) + get_distance_int32(config, context, j, u) + get_distance_int32(config, context, u, j_next));
        if (gain_i > 0) context.path_distance_int32 -= gain_i;
    } else if (config.distance_type == DistanceType::Int64) {
        long long gain_ll =
            get_distance_int64(config, context, p, u) + get_distance_int64(config, context, u, n) + get_distance_int64(config, context, j, j_next)
            - (get_distance_int64(config, context, p, n) + get_distance_int64(config, context, j, u) + get_distance_int64(config, context, u, j_next));
        if (gain_ll > 0) context.path_distance_int64 -= gain_ll;
    }

    return true;
}

