#pragma once


bool apply_2_opt_move(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) { return false; }

    int i_next = context.path[i].next;
    int j_next = context.path[j].next;

    ++context.chosen_times[i * config.cities_number + j];
    ++context.chosen_times[j * config.cities_number + i];
    ++context.chosen_times[i_next * config.cities_number + j_next];
    ++context.chosen_times[j_next * config.cities_number + i_next];

    ++context.total_simulations;

    // Always decide in DOUBLE to avoid quantization rejecting small but real gains.
    const double d_ii = get_distance_double(config, context, i, i_next);
    const double d_jj = get_distance_double(config, context, j, j_next);
    const double d_ij = get_distance_double(config, context, i, j);
    const double d_injn = get_distance_double(config, context, i_next, j_next);
    const double delta_d = (d_ii + d_jj) - (d_ij + d_injn);

    if (delta_d <= 0.0) { return false; } // no improvement in true Euclidean metric

    // Compute weight increase normalized by pre-move length (double).
    const double prev_len_d = context.path_distance_double;
    double weight_increase = config.weight_delta_coefficient * (std::exp(delta_d / prev_len_d) - 1.0);

    // Maintain integer bookkeeping if we run in int32/int64 mode (for k-opt internals),
    // but accept the move based on DOUBLE.
    if (config.distance_type == DistanceType::Int32) {
        const int dii = get_distance_int32(config, context, i, i_next);
        const int djj = get_distance_int32(config, context, j, j_next);
        const int dij = get_distance_int32(config, context, i, j);
        const int dinjn = get_distance_int32(config, context, i_next, j_next);
        const int delta_i = (dii + djj) - (dij + dinjn);
        if (delta_i > 0) { context.path_distance_int32 -= delta_i; } // if it rounds to 0, keep as-is
    } else if (config.distance_type == DistanceType::Int64) {
        const long long dii = get_distance_int64(config, context, i, i_next);
        const long long djj = get_distance_int64(config, context, j, j_next);
        const long long dij = get_distance_int64(config, context, i, j);
        const long long dinjn = get_distance_int64(config, context, i_next, j_next);
        const long long delta_i = (dii + djj) - (dij + dinjn);
        if (delta_i > 0) { context.path_distance_int64 -= delta_i; }
    }

    // Update the double tour length exactly.
    context.path_distance_double -= delta_d;

    // Apply the 2-opt reversal.
    reverse_sub_path(context, i_next, j);
    context.path[i].next = j;
    context.path[i_next].next = j_next;
    context.path[j].prev = i;
    context.path[j_next].prev = i_next;

    // Update weights for the two created edges.
    update_weight_undirected(config, context, i, j, weight_increase);
    update_weight_undirected(config, context, i_next, j_next, weight_increase);

    return true;
}


bool improve_by_2_opt_move(const Config& config, Context& context) {
    for (int i = 0; i < config.cities_number; ++i) {
		for (int j = 0; j < config.candidates_number; ++j) {
			int candidate = context.candidates[i * config.candidates_number + j];

			if (apply_2_opt_move(config, context, i, candidate)) { return true; }
		}
	}
    return false;
}

int local_2_opt_search(const Config& config, Context& context) {
    // Lin–Kernighan “don’t-look” bits: skip cities that didn’t improve in the last sweep.
    const int n = config.cities_number;
    for (int i = 0; i < n; ++i) context.dont_look[i] = 0;

    int improved_times = 0;

    while (true) {
        bool improved_this_sweep = false;

        for (int i = 0; i < n; ++i) {
            if (context.dont_look[i]) continue;

            bool improved_city = false;

            // 1) Try standard KNN(i).
            for (int j = 0; j < config.candidates_number; ++j) {
                int cand = context.candidates[i * config.candidates_number + j];
                if (apply_2_opt_move(config, context, i, cand)) {
                    context.dont_look[i] = 0;
                    context.dont_look[cand] = 0;
                    int inext = context.path[i].next;
                    int cnext = context.path[cand].next;
                    if (inext != null) context.dont_look[inext] = 0;
                    if (cnext != null) context.dont_look[cnext] = 0;

                    ++improved_times;
                    improved_city = true;
                    improved_this_sweep = true;
                    break;
                }
            }

            // 2) If no luck, try union-KNN with successor(i).
            if (!improved_city) {
                int inext = context.path[i].next;
                if (inext != null) {
                    int limit = std::min(config.candidates_number, 12); // small tail for speed
                    for (int j = 0; j < limit; ++j) {
                        int cand = context.candidates[inext * config.candidates_number + j];
                        if (apply_2_opt_move(config, context, i, cand)) {
                            context.dont_look[i] = 0;
                            context.dont_look[cand] = 0;
                            int inext2 = context.path[i].next;
                            int cnext2 = context.path[cand].next;
                            if (inext2 != null) context.dont_look[inext2] = 0;
                            if (cnext2 != null) context.dont_look[cnext2] = 0;

                            ++improved_times;
                            improved_city = true;
                            improved_this_sweep = true;
                            break;
                        }
                    }
                }
            }

            // 3) Cheap diversification: a few random probes.
            if (!improved_city) {
                for (int r = 0; r < 8; ++r) {
                    int cand = get_random_int_by_module(n);
                    if (apply_2_opt_move(config, context, i, cand)) {
                        context.dont_look[i] = 0;
                        context.dont_look[cand] = 0;
                        int inext = context.path[i].next;
                        int cnext = context.path[cand].next;
                        if (inext != null) context.dont_look[inext] = 0;
                        if (cnext != null) context.dont_look[cnext] = 0;

                        ++improved_times;
                        improved_city = true;
                        improved_this_sweep = true;
                        break;
                    }
                }
            }

            if (!improved_city) {
                context.dont_look[i] = 1;
            }
        }

        if (!improved_this_sweep) break;
    }

    return improved_times;
}
