#pragma once

// Additional heuristics to improve basin exploration & tour quality.

#include <algorithm>
#include <vector>
#include <cstdlib>
#include <cmath>

// Double-Bridge perturbation (classic 4-opt kick).
// Takes current path in `context.path`, converts to permutation,
// applies 4-cut double-bridge, writes back to path.
inline void apply_double_bridge_perturbation(const Config& config, Context& context, int min_segment = 5) {
    // Make sure we have a valid cycle -> solution
    convert_path_to_solution(config, context);

    const int n = config.cities_number;
    if (n < 12) {  // too small to meaningfully cut 4 segments
        convert_solution_to_path(config, context);
        return;
    }

    // Ensure segments are reasonably sized
    int gap = std::max(min_segment, n / 20);

    int a = 1 + (std::rand() % (n - 4*gap - 1));
    int b = a + gap + (std::rand() % gap);
    int c = b + gap + (std::rand() % gap);
    int d = c + gap + (std::rand() % gap);

    if (d >= n) { d = n - 1; }
    if (!(0 < a && a < b && b < c && c < d && d < n)) {
        convert_solution_to_path(config, context);
        return;
    }

    std::vector<int> &s = *reinterpret_cast<std::vector<int>*>(&context.buffer); // reuse capacity guard
    s.assign(context.solution, context.solution + n);

    std::vector<int> out;
    out.reserve(n);

    // New order: [0..a) + [c..d) + [b..c) + [d..n) + [a..b)
    out.insert(out.end(), s.begin(), s.begin() + a);
    out.insert(out.end(), s.begin() + c, s.begin() + d);
    out.insert(out.end(), s.begin() + b, s.begin() + c);
    out.insert(out.end(), s.begin() + d, s.end());
    out.insert(out.end(), s.begin() + a, s.begin() + b);

    for (int i = 0; i < n; ++i) context.solution[i] = out[i];
    convert_solution_to_path(config, context);
}

// Or-opt (1-node relocation) between 2-opt and k-opt.
// Relocates vertex `a` from (u,a,v) to right after u_ins (before un) if gain > 0.
// Complexity: ~O(n * K) per sweep. Gives reliable -0.1…-0.3 on n=500.
inline int local_or_opt_search(const Config& config, Context& context) {
    const int n = config.cities_number;
    int improved_times = 0;

    bool improved = true;
    while (improved) {
        improved = false;

        for (int a = 0; a < n; ++a) {
            int u = context.path[a].prev;
            int v = context.path[a].next;
            if (u == null || v == null) continue; // should not happen in a cycle, but be safe

            bool moved_this_city = false;

            for (int idx = 0; idx < config.candidates_number; ++idx) {
                int u_ins = context.candidates[a * config.candidates_number + idx];
                if (u_ins == a || u_ins == u || u_ins == v) continue;

                int un = context.path[u_ins].next;
                if (un == a || un == null) continue;

                bool apply = false;
                double weight_increase = 0.0;

                if (config.distance_type == DistanceType::Double) {
                    double old_cost =
                        get_distance_double(config, context, u, a) +
                        get_distance_double(config, context, a, v) +
                        get_distance_double(config, context, u_ins, un);

                    double new_cost =
                        get_distance_double(config, context, u, v) +
                        get_distance_double(config, context, u_ins, a) +
                        get_distance_double(config, context, a, un);

                    double gain = old_cost - new_cost;
                    if (gain > 0.0) {
                        // Path length update
                        context.path_distance_double -= gain;
                        // Weight shaping (normalized by pre-move length)
                        double denom = context.path_distance_double + gain; // previous length
                        weight_increase = config.weight_delta_coefficient * (std::exp(gain / denom) - 1.0);
                        apply = true;
                    }
                } else if (config.distance_type == DistanceType::Int32) {
                    int old_cost =
                        get_distance_int32(config, context, u, a) +
                        get_distance_int32(config, context, a, v) +
                        get_distance_int32(config, context, u_ins, un);

                    int new_cost =
                        get_distance_int32(config, context, u, v) +
                        get_distance_int32(config, context, u_ins, a) +
                        get_distance_int32(config, context, a, un);

                    int gain = old_cost - new_cost;
                    if (gain > 0) {
                        context.path_distance_int32 -= gain;
                        double denom = static_cast<double>(context.path_distance_int32 + gain);
                        weight_increase = config.weight_delta_coefficient * (std::exp(static_cast<double>(gain) / denom) - 1.0);
                        apply = true;
                    }
                } else { // Int64
                    long long old_cost =
                        get_distance_int64(config, context, u, a) +
                        get_distance_int64(config, context, a, v) +
                        get_distance_int64(config, context, u_ins, un);

                    long long new_cost =
                        get_distance_int64(config, context, u, v) +
                        get_distance_int64(config, context, u_ins, a) +
                        get_distance_int64(config, context, a, un);

                    long long gain = old_cost - new_cost;
                    if (gain > 0) {
                        context.path_distance_int64 -= gain;
                        double denom = static_cast<double>(context.path_distance_int64 + gain);
                        weight_increase = config.weight_delta_coefficient * (std::exp(static_cast<double>(gain) / denom) - 1.0);
                        apply = true;
                    }
                }

                if (apply) {
                    // Unlink a from (u,a,v)
                    context.path[u].next = v;
                    context.path[v].prev = u;

                    // Insert a after u_ins (before un)
                    context.path[a].prev = u_ins;
                    context.path[a].next = un;
                    context.path[u_ins].next = a;
                    context.path[un].prev = a;

                    // Weight updates on newly created edges
                    update_weight_undirected(config, context, u, v, weight_increase);
                    update_weight_undirected(config, context, u_ins, a, weight_increase);
                    update_weight_undirected(config, context, a, un, weight_increase);

                    ++improved_times;
                    moved_this_city = true;
                    improved = true;
                    break; // restart scanning cities (fast convergence)
                }
            }

            if (moved_this_city) break;
        }
    }

    // Keep double snapshot consistent for reporting/threshold if we’re in int mode
    if (config.distance_type != DistanceType::Double) {
        context.path_distance_double = calc_total_distance_double(config, context);
    }

    return improved_times;
}
