#pragma once

// Greedy KNN-based tour constructor with global fallback.
// Uses squared distances (no sqrt); order is preserved.
inline long double _dist2_idx(const Config& config, Context& context, int a, int b) {
    if (config.distance_type == DistanceType::Double) {
        long double dx = (long double)context.coordinates_double_x[a] - (long double)context.coordinates_double_x[b];
        long double dy = (long double)context.coordinates_double_y[a] - (long double)context.coordinates_double_y[b];
        return dx * dx + dy * dy;
    } else if (config.distance_type == DistanceType::Int32) {
        long long dx = (long long)context.coordinates_int32_x[a] - (long long)context.coordinates_int32_x[b];
        long long dy = (long long)context.coordinates_int32_y[a] - (long long)context.coordinates_int32_y[b];
        return (long double)dx * dx + (long double)dy * dy;
    } else {
        long long dx = context.coordinates_int64_x[a] - context.coordinates_int64_x[b];
        long long dy = context.coordinates_int64_y[a] - context.coordinates_int64_y[b];
        return (long double)dx * dx + (long double)dy * dy;
    }
}

inline void generate_greedy_nn_solution(const Config& config, Context& context) {
    const int n = config.cities_number;

    for (int i = 0; i < n; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    int start = get_random_int_by_module(n);
    context.solution[0] = start;
    context.is_city_selected[start] = true;

    int cur = start;
    for (int pos = 1; pos < n; ++pos) {
        int best = null;
        long double bestd = std::numeric_limits<long double>::infinity();

        // Try KNN list first
        for (int k = 0; k < config.candidates_number; ++k) {
            int v = context.candidates[cur * config.candidates_number + k];
            if (v == cur || context.is_city_selected[v]) continue;
            long double d2 = _dist2_idx(config, context, cur, v);
            if (d2 < bestd) { bestd = d2; best = v; }
        }

        // Fallback: full scan
        if (best == null) {
            for (int v = 0; v < n; ++v) {
                if (v == cur || context.is_city_selected[v]) continue;
                long double d2 = _dist2_idx(config, context, cur, v);
                if (d2 < bestd) { bestd = d2; best = v; }
            }
        }

        context.solution[pos] = best;
        context.is_city_selected[best] = true;
        cur = best;
    }
}

// ---------- Or-opt (1-node relocation) ----------
inline int _get_d(const Config& config, Context& context, int a, int b) {
    if (config.distance_type == DistanceType::Double) {
        return 0; // unused in double branch
    } else if (config.distance_type == DistanceType::Int32) {
        return get_distance_int32(config, context, a, b);
    } else {
        // Int64: we will cast to long long separately; keep signature uniform
        return 0;
    }
}

// Compute edge length in active metric as double for delta arithmetic.
inline double _edge_len(const Config& config, Context& context, int a, int b) {
    if (config.distance_type == DistanceType::Double) {
        return get_distance_double(config, context, a, b);
    } else if (config.distance_type == DistanceType::Int32) {
        return static_cast<double>(get_distance_int32(config, context, a, b));
    } else {
        return static_cast<double>(get_distance_int64(config, context, a, b));
    }
}

// Insert city `a` immediately after city `u` (before u_next). Returns true if improved and applies the move.
inline bool apply_or_opt_1(const Config& config, Context& context, int a, int u) {
    if (a == u) return false;
    int a_prev = context.path[a].prev;
    int a_next = context.path[a].next;
    int u_next = context.path[u].next;

    // Skip adjacent placements that become no-ops or degenerate
    if (a == u_next || u == a_prev || u == a_next) return false;

    // Delta = removed - added
    // Removed: (a_prev,a) + (a,a_next) + (u,u_next)
    // Added  : (a_prev,a_next) + (u,a) + (a,u_next)
    double removed = _edge_len(config, context, a_prev, a) + _edge_len(config, context, a, a_next) + _edge_len(config, context, u, u_next);
    double added   = _edge_len(config, context, a_prev, a_next) + _edge_len(config, context, u, a) + _edge_len(config, context, a, u_next);
    double delta = removed - added;

    if (delta <= 0.0) return false;

    // Detach a
    context.path[a_prev].next = a_next;
    context.path[a_next].prev = a_prev;

    // Insert after u
    context.path[a].prev = u;
    context.path[a].next = u_next;
    context.path[u].next = a;
    context.path[u_next].prev = a;

    // Update path distance in active metric
    if (config.distance_type == DistanceType::Double) {
        context.path_distance_double -= delta;
    } else if (config.distance_type == DistanceType::Int32) {
        context.path_distance_int32 -= static_cast<int>(std::llround(delta));
    } else {
        context.path_distance_int64 -= static_cast<long long>(std::llround(delta));
    }

    return true;
}

// Repeatedly apply first-improvement Or-opt(1) using KNN anchors.
// Returns number of improvements applied.
inline int local_or_opt_search(const Config& config, Context& context) {
    const int n = config.cities_number;
    int improved_times = 0;
    bool improved = true;

    while (improved) {
        improved = false;
        for (int a = 0; a < n; ++a) {
            for (int k = 0; k < config.candidates_number; ++k) {
                int u = context.candidates[a * config.candidates_number + k];
                if (apply_or_opt_1(config, context, a, u)) {
                    ++improved_times;
                    improved = true;
                    // restart sweep from scratch (first-improvement strategy)
                    goto next_sweep;
                }
            }
        }
    next_sweep:
        (void)0;
    }
    return improved_times;
}
