// Insert city `a` immediately after city `u` (before u_next). Returns true if improved and applies the move.
inline bool apply_or_opt_1(const Config& config, Context& context, int a, int u) {
    if (a == u) return false;
    int a_prev = context.path[a].prev;
    int a_next = context.path[a].next;
    int u_next = context.path[u].next;

    // Skip adjacent placements that become no-ops or degenerate
    if (a == u_next || u == a_prev || u == a_next) return false;

    // Delta = removed - added
    // Removed: (a_prev,a) + (a,a_next) + (u,u_next)
    // Added  : (a_prev,a_next) + (u,a) + (a,u_next)
    double removed = _edge_len(config, context, a_prev, a) + _edge_len(config, context, a, a_next) + _edge_len(config, context, u, u_next);
    double added   = _edge_len(config, context, a_prev, a_next) + _edge_len(config, context, u, a) + _edge_len(config, context, a, u_next);
    double delta = removed - added;

    if (delta <= 0.0) return false;

    // Detach a
    context.path[a_prev].next = a_next;
    context.path[a_next].prev = a_prev;

    // Insert after u
    context.path[a].prev = u;
    context.path[a].next = u_next;
    context.path[u].next = a;
    context.path[u_next].prev = a;

    // Update path distance in active metric
    if (config.distance_type == DistanceType::Double) {
        context.path_distance_double -= delta;
    } else if (config.distance_type == DistanceType::Int32) {
        context.path_distance_int32 -= static_cast<int>(std::llround(delta));
    } else {
        context.path_distance_int64 -= static_cast<long long>(std::llround(delta));
    }

    return true;
}

// Repeatedly apply first-improvement Or-opt(1) using KNN anchors.
// Returns number of improvements applied.
inline int local_or_opt_search(const Config& config, Context& context) {
    const int n = config.cities_number;
    int improved_times = 0;
    bool improved = true;

    while (improved) {
        improved = false;
        for (int a = 0; a < n; ++a) {
            for (int k = 0; k < config.candidates_number; ++k) {
                int u = context.candidates[a * config.candidates_number + k];
                if (apply_or_opt_1(config, context, a, u)) {
                    ++improved_times;
                    improved = true;
                    // restart sweep from scratch (first-improvement strategy)
                    goto next_sweep;
                }
            }
        }
    next_sweep:
        (void)0;
    }
    return improved_times;
}
