#pragma once


// * The following functions are used to randomly generate an initial solution.
// * Starting from an arbitrarily chosen city, it iteratively selects a random city until forming a complete TSP tour.


int select_random_city(const Config& config, Context& context) {
	int random_start = get_random_int_by_module(config.cities_number);

	for (int i = 0; i < config.cities_number; ++i) {
		int city = (random_start + i) % config.cities_number;

		if (!context.is_city_selected[city]) {
			return city;
		}
	}

	return null;
}

void generate_random_solution(const Config& config, Context& context) {
	for (int i = 0; i < config.cities_number; ++i) {
		context.solution[i] = null;
		context.is_city_selected[i] = false;
	}

    for (int i = 0; i < config.cities_number; ++i) {
        int current_city = select_random_city(config, context);
        context.solution[i] = current_city;
        context.is_city_selected[current_city] = true;
    }
}

// Greedy nearest-neighbor initializer over the candidate set (falls back to full scan when needed).
void generate_greedy_nn_solution(const Config& config, Context& context) {
    // reset
    for (int i = 0; i < config.cities_number; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    int start = get_random_int_by_module(config.cities_number);
    int current = start;

    for (int idx = 0; idx < config.cities_number; ++idx) {
        context.solution[idx] = current;
        context.is_city_selected[current] = true;

        // choose nearest unvisited among candidates
        int best = null;
        for (int k = 0; k < config.candidates_number; ++k) {
            int cand = context.candidates[current * config.candidates_number + k];
            if (cand == current || context.is_city_selected[cand]) continue;

            if (best == null) {
                best = cand;
            } else {
                if (config.distance_type == DistanceType::Int32) {
                    if (get_distance_int32(config, context, current, cand) < get_distance_int32(config, context, current, best)) best = cand;
                } else if (config.distance_type == DistanceType::Int64) {
                    if (get_distance_int64(config, context, current, cand) < get_distance_int64(config, context, current, best)) best = cand;
                } else {
                    // distance_double holds squared distances; ranking is preserved
                    if (get_distance_double(config, context, current, cand) < get_distance_double(config, context, current, best)) best = cand;
                }
            }
        }

        // fallback: full scan if all candidates already used
        if (best == null) {
            for (int v = 0; v < config.cities_number; ++v) {
                if (v == current || context.is_city_selected[v]) continue;
                if (best == null) {
                    best = v;
                } else {
                    if (config.distance_type == DistanceType::Int32) {
                        if (get_distance_int32(config, context, current, v) < get_distance_int32(config, context, current, best)) best = v;
                    } else if (config.distance_type == DistanceType::Int64) {
                        if (get_distance_int64(config, context, current, v) < get_distance_int64(config, context, current, best)) best = v;
                    } else {
                        if (calc_distance_double(context, current, v) < calc_distance_double(context, current, best)) best = v;
                    }
                }
            }
        }

        if (best == null) {
            break; // only possible at the very end
        }
        current = best;
    }
}
