#pragma once


bool apply_2_opt_move(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) { return false; }

    int i_next = context.path[i].next;
    int j_next = context.path[j].next;

    ++context.chosen_times[i * config.cities_number + j];
    ++context.chosen_times[j * config.cities_number + i];
    ++context.chosen_times[i_next * config.cities_number + j_next];
    ++context.chosen_times[j_next * config.cities_number + i_next];

    ++context.total_simulations;

    // calculating delta
    bool apply_move = false;
    double weight_increase = 0.0;

    if (config.distance_type == DistanceType::Double) {
        double delta = get_distance_double(config, context, i, i_next) +
                       get_distance_double(config, context, j, j_next) -
                       get_distance_double(config, context, i, j) -
                       get_distance_double(config, context, i_next, j_next);
        if (delta > 0.0) {
            apply_move = true;
            weight_increase = config.weight_delta_coefficient * std::expm1(delta / context.path_distance_double);
            context.path_distance_double -= delta;
        }
    }
    if (config.distance_type == DistanceType::Int32) {
        int delta = get_distance_int32(config, context, i, i_next) +
                    get_distance_int32(config, context, j, j_next) -
                    get_distance_int32(config, context, i, j) -
                    get_distance_int32(config, context, i_next, j_next);
        if (delta > 0) {
            apply_move = true;
            weight_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(delta) / context.path_distance_int32);
            context.path_distance_int32 -= delta;
        }
    }
    if (config.distance_type == DistanceType::Int64) {
        long long delta = get_distance_int64(config, context, i, i_next) +
                          get_distance_int64(config, context, j, j_next) -
                          get_distance_int64(config, context, i, j) -
                          get_distance_int64(config, context, i_next, j_next);
        if (delta > 0) {
            apply_move = true;
            weight_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(delta) / context.path_distance_int64);
            context.path_distance_int64 -= delta;
        }
    }

    if (apply_move) {
        // applying 2 opt move
        reverse_sub_path(context, i_next, j);

        context.path[i].next = j;
        context.path[i_next].next = j_next;
        context.path[j].prev = i;
        context.path[j_next].prev = i_next;

        // updating weights (reinforce new edges)
        update_weight_undirected(config, context, i, j, weight_increase);
        update_weight_undirected(config, context, i_next, j_next, weight_increase);

        return true;
    }
    return false;
}


/*
 * Steepest 2-opt (pick best (i,j) over candidate list), then Or-opt(1) relocate when 2-opt stalls.
 * Formulas:
 *   2-opt gain Δ2 = d(i,i') + d(j,j') - d(i,j) - d(i',j')
 *   Or-opt(1) gain ΔOr1 = d(p,u) + d(u,n) + d(j,j') - [ d(p,n) + d(j,u) + d(u,j') ]
 * Accept if Δ>0 (reduces tour length).
 */
static inline double edge_cost_double(const Config& config, Context& context, int a, int b) {
    // use precomputed true Euclidean distances
    return get_distance_double(config, context, a, b);
}
static inline int edge_cost_int32(const Config& config, Context& context, int a, int b) {
    return get_distance_int32(config, context, a, b);
}
static inline long long edge_cost_int64(const Config& config, Context& context, int a, int b) {
    return get_distance_int64(config, context, a, b);
}

bool improve_by_2_opt_move(const Config& config, Context& context) {
    int best_i = null, best_j = null;
    double best_gain_double = 0.0;
    int    best_gain_int32  = 0;
    long long best_gain_int64 = 0;

    for (int i = 0; i < config.cities_number; ++i) {
        int i_next = context.path[i].next;

        // scan KNN(i)
        for (int j = 0; j < config.candidates_number; ++j) {
            int cand = context.candidates[i * config.candidates_number + j];
            if (is_cities_same_or_adjacent(config, context, i, cand)) continue;

            int j_next = context.path[cand].next;

            if (config.distance_type == DistanceType::Double) {
                double gain = edge_cost_double(config, context, i, i_next)
                            + edge_cost_double(config, context, cand, j_next)
                            - edge_cost_double(config, context, i, cand)
                            - edge_cost_double(config, context, i_next, j_next);
                if (gain > best_gain_double) { best_gain_double = gain; best_i = i; best_j = cand; }
            } else if (config.distance_type == DistanceType::Int32) {
                int gain = edge_cost_int32(config, context, i, i_next)
                         + edge_cost_int32(config, context, cand, j_next)
                         - edge_cost_int32(config, context, i, cand)
                         - edge_cost_int32(config, context, i_next, j_next);
                if (gain > best_gain_int32) { best_gain_int32 = gain; best_i = i; best_j = cand; }
            } else {
                long long gain = edge_cost_int64(config, context, i, i_next)
                               + edge_cost_int64(config, context, cand, j_next)
                               - edge_cost_int64(config, context, i, cand)
                               - edge_cost_int64(config, context, i_next, j_next);
                if (gain > best_gain_int64) { best_gain_int64 = gain; best_i = i; best_j = cand; }
            }
        }

        // additionally scan KNN(next(i)) to widen the neighborhood
        for (int j = 0; j < config.candidates_number; ++j) {
            int cand = context.candidates[i_next * config.candidates_number + j];
            if (is_cities_same_or_adjacent(config, context, i, cand)) continue;

            int j_next = context.path[cand].next;

            if (config.distance_type == DistanceType::Double) {
                double gain = edge_cost_double(config, context, i, i_next)
                            + edge_cost_double(config, context, cand, j_next)
                            - edge_cost_double(config, context, i, cand)
                            - edge_cost_double(config, context, i_next, j_next);
                if (gain > best_gain_double) { best_gain_double = gain; best_i = i; best_j = cand; }
            } else if (config.distance_type == DistanceType::Int32) {
                int gain = edge_cost_int32(config, context, i, i_next)
                         + edge_cost_int32(config, context, cand, j_next)
                         - edge_cost_int32(config, context, i, cand)
                         - edge_cost_int32(config, context, i_next, j_next);
                if (gain > best_gain_int32) { best_gain_int32 = gain; best_i = i; best_j = cand; }
            } else {
                long long gain = edge_cost_int64(config, context, i, i_next)
                               + edge_cost_int64(config, context, cand, j_next)
                               - edge_cost_int64(config, context, i, cand)
                               - edge_cost_int64(config, context, i_next, j_next);
                if (gain > best_gain_int64) { best_gain_int64 = gain; best_i = i; best_j = cand; }
            }
        }
    }

    if (best_i != null) {
        return apply_2_opt_move(config, context, best_i, best_j);
    }
    return false;
}

// --- Or-opt(1): relocate single node u between (j,j') ---
bool apply_or_opt1_move(const Config& config, Context& context, int u, int j) {
    int p = context.path[u].prev;
    int n = context.path[u].next;
    int jn = context.path[j].next;

    if (u == j || u == jn || j == p || j == n || p == jn || n == j) return false; // trivial/adjacent conflicts

    double w_increase = 0.0;
    bool apply = false;

    if (config.distance_type == DistanceType::Double) {
        double old_cost = edge_cost_double(config, context, p, u) + edge_cost_double(config, context, u, n) + edge_cost_double(config, context, j, jn);
        double new_cost = edge_cost_double(config, context, p, n) + edge_cost_double(config, context, j, u) + edge_cost_double(config, context, u, jn);
        double gain = old_cost - new_cost;
        if (gain > 0.0) {
            apply = true;
            context.path_distance_double -= gain;
            w_increase = config.weight_delta_coefficient * std::expm1(gain / (context.path_distance_double + gain));
        }
    } else if (config.distance_type == DistanceType::Int32) {
        int old_cost = edge_cost_int32(config, context, p, u) + edge_cost_int32(config, context, u, n) + edge_cost_int32(config, context, j, jn);
        int new_cost = edge_cost_int32(config, context, p, n) + edge_cost_int32(config, context, j, u) + edge_cost_int32(config, context, u, jn);
        int gain = old_cost - new_cost;
        if (gain > 0) {
            apply = true;
            context.path_distance_int32 -= gain;
            w_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(gain) / (context.path_distance_int32 + gain));
        }
    } else {
        long long old_cost = edge_cost_int64(config, context, p, u) + edge_cost_int64(config, context, u, n) + edge_cost_int64(config, context, j, jn);
        long long new_cost = edge_cost_int64(config, context, p, n) + edge_cost_int64(config, context, j, u) + edge_cost_int64(config, context, u, jn);
        long long gain = old_cost - new_cost;
        if (gain > 0) {
            apply = true;
            context.path_distance_int64 -= gain;
            w_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(gain) / (context.path_distance_int64 + gain));
        }
    }

    if (!apply) return false;

    // detach u from (p,u,n)
    context.path[p].next = n;
    context.path[n].prev = p;

    // insert u between (j, jn)
    context.path[j].next = u;
    context.path[u].prev = j;
    context.path[u].next = jn;
    context.path[jn].prev = u;

    // reinforce new edges
    update_weight_undirected(config, context, p, n, w_increase);
    update_weight_undirected(config, context, j, u, w_increase);
    update_weight_undirected(config, context, u, jn, w_increase);
    return true;
}

bool improve_by_or_opt1_relocate(const Config& config, Context& context) {
    int best_u = null, best_j = null;
    double best_gain_d = 0.0; int best_gain_i = 0; long long best_gain_l = 0;

    for (int u = 0; u < config.cities_number; ++u) {
        for (int k = 0; k < config.candidates_number; ++k) {
            int j = context.candidates[u * config.candidates_number + k];
            int p = context.path[u].prev, n = context.path[u].next, jn = context.path[j].next;
            if (u == j || u == jn || j == p || j == n || p == jn || n == j) continue;

            if (config.distance_type == DistanceType::Double) {
                double oldc = edge_cost_double(config, context, p, u) + edge_cost_double(config, context, u, n) + edge_cost_double(config, context, j, jn);
                double newc = edge_cost_double(config, context, p, n) + edge_cost_double(config, context, j, u) + edge_cost_double(config, context, u, jn);
                double gain = oldc - newc;
                if (gain > best_gain_d) { best_gain_d = gain; best_u = u; best_j = j; }
            } else if (config.distance_type == DistanceType::Int32) {
                int oldc = edge_cost_int32(config, context, p, u) + edge_cost_int32(config, context, u, n) + edge_cost_int32(config, context, j, jn);
                int newc = edge_cost_int32(config, context, p, n) + edge_cost_int32(config, context, j, u) + edge_cost_int32(config, context, u, jn);
                int gain = oldc - newc;
                if (gain > best_gain_i) { best_gain_i = gain; best_u = u; best_j = j; }
            } else {
                long long oldc = edge_cost_int64(config, context, p, u) + edge_cost_int64(config, context, u, n) + edge_cost_int64(config, context, j, jn);
                long long newc = edge_cost_int64(config, context, p, n) + edge_cost_int64(config, context, j, u) + edge_cost_int64(config, context, u, jn);
                long long gain = oldc - newc;
                if (gain > best_gain_l) { best_gain_l = gain; best_u = u; best_j = j; }
            }
        }
    }
    if (best_u != null) {
        return apply_or_opt1_move(config, context, best_u, best_j);
    }
    return false;
}

int local_2_opt_search(const Config& config, Context& context) {
    int improved_times = 0;
    while (true) {
        bool any = false;
        if (improve_by_2_opt_move(config, context)) { ++improved_times; any = true; }
        else if (improve_by_or_opt1_relocate(config, context)) { ++improved_times; any = true; }
        if (!any) break;
    }
    return improved_times;
}
