#pragma once

// Double-bridge perturbation (ILS kick) to escape deep local minima.
// Cuts the tour into four segments and reconnects as S0 + S2 + S1 + S3 + S4.
inline void double_bridge_kick(const Config& config, Context& context) {
    const int n = config.cities_number;
    if (n < 8) return;

    // Work on the solution sequence.
    convert_path_to_solution(config, context);
    std::vector<int> sol(n);
    for (int i = 0; i < n; ++i) sol[i] = context.solution[i];

    int i1 = 1 + rand() % (n / 5);
    int i2 = i1 + 1 + rand() % (n / 5);
    int i3 = i2 + 1 + rand() % (n / 5);
    int i4 = i3 + 1 + rand() % (n / 5);
    if (i4 >= n) i4 = n - 1;

    std::vector<int> s0(sol.begin(), sol.begin() + i1);
    std::vector<int> s1(sol.begin() + i1, sol.begin() + i2);
    std::vector<int> s2(sol.begin() + i2, sol.begin() + i3);
    std::vector<int> s3(sol.begin() + i3, sol.begin() + i4);
    std::vector<int> s4(sol.begin() + i4, sol.end());

    std::vector<int> new_sol;
    new_sol.reserve(n);
    new_sol.insert(new_sol.end(), s0.begin(), s0.end());
    new_sol.insert(new_sol.end(), s2.begin(), s2.end());
    new_sol.insert(new_sol.end(), s1.begin(), s1.end());
    new_sol.insert(new_sol.end(), s3.begin(), s3.end());
    new_sol.insert(new_sol.end(), s4.begin(), s4.end());

    for (int i = 0; i < n; ++i) context.solution[i] = new_sol[i];
    convert_solution_to_path(config, context);
    calc_and_save_total_distance(config, context);
}
