    std::vector<int> s1(sol.begin() + i1, sol.begin() + i2);
    std::vector<int> s2(sol.begin() + i2, sol.begin() + i3);
    std::vector<int> s3(sol.begin() + i3, sol.begin() + i4);
    std::vector<int> s4(sol.begin() + i4, sol.end());

    std::vector<int> new_sol;
    new_sol.reserve(n);
    new_sol.insert(new_sol.end(), s0.begin(), s0.end());
    new_sol.insert(new_sol.end(), s2.begin(), s2.end());
    new_sol.insert(new_sol.end(), s1.begin(), s1.end());
    new_sol.insert(new_sol.end(), s3.begin(), s3.end());
    new_sol.insert(new_sol.end(), s4.begin(), s4.end());

