#pragma once


bool apply_2_opt_move(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) { return false; }

    int i_next = context.path[i].next;
	int j_next = context.path[j].next;

    ++context.chosen_times[i * config.cities_number + j];
	++context.chosen_times[j * config.cities_number + i];
	++context.chosen_times[i_next * config.cities_number + j_next];
	++context.chosen_times[j_next * config.cities_number + i_next];

    ++context.total_simulations;

    // calculating delta
    bool apply_move = false;
    double weight_increase = 0.0;

    if (config.distance_type == DistanceType::Double) {
        double delta = get_distance_double(config, context, i, i_next) + get_distance_double(config, context, j, j_next) - get_distance_double(config, context, i, j) - get_distance_double(config, context, i_next, j_next);
        if (delta > 0.0) {
            apply_move = true;
            weight_increase = config.weight_delta_coefficient * std::expm1(delta / context.path_distance_double);
            context.path_distance_double -= delta;
        }
    }
    if (config.distance_type == DistanceType::Int32) {
        int delta = get_distance_int32(config, context, i, i_next) + get_distance_int32(config, context, j, j_next) - get_distance_int32(config, context, i, j) - get_distance_int32(config, context, i_next, j_next);
        if (delta > 0) {
            apply_move = true;
            weight_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(delta) / context.path_distance_int32);
            context.path_distance_int32 -= delta;
        }
    }
    if (config.distance_type == DistanceType::Int64) {
        long long delta = get_distance_int64(config, context, i, i_next) + get_distance_int64(config, context, j, j_next) - get_distance_int64(config, context, i, j) - get_distance_int64(config, context, i_next, j_next);
        if (delta > 0) {
            apply_move = true;
            weight_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(delta) / context.path_distance_int64);
            context.path_distance_int64 -= delta;
        }
    }

    if (apply_move) {
        // applying 2 opt move
        reverse_sub_path(context, i_next, j);

        context.path[i].next = j;
        context.path[i_next].next = j_next;
        context.path[j].prev = i;
        context.path[j_next].prev = i_next;

        // updating weights
        update_weight_undirected(config, context, i, j, weight_increase);
        update_weight_undirected(config, context, i_next, j_next, weight_increase);

        return true;
    }
    return false;
}


bool improve_by_2_opt_move(const Config& config, Context& context) {
    // Steepest-improvement 2-opt with mutual-KNN gating
    int best_i = -1, best_j = -1;
    double best_gain_d = 0.0;
    long long best_gain_ll = 0;
    int best_gain_i32 = 0;

    for (int i = 0; i < config.cities_number; ++i) {
        int i_next = context.path[i].next;
        for (int jj = 0; jj < config.candidates_number; ++jj) {
            int j = context.candidates[i * config.candidates_number + jj];
            if (is_cities_same_or_adjacent(config, context, i, j)) continue;

            // mutual-KNN gate: require i in candidates of j
            bool mutual = false;
            for (int kk = 0; kk < config.candidates_number; ++kk) {
                if (context.candidates[j * config.candidates_number + kk] == i) { mutual = true; break; }
            }
            if (!mutual) continue;

            int j_next = context.path[j].next;

            if (config.distance_type == DistanceType::Double) {
                double delta = get_distance_double(config, context, i, i_next) + get_distance_double(config, context, j, j_next)
                             - get_distance_double(config, context, i, j) - get_distance_double(config, context, i_next, j_next);
                if (delta > best_gain_d) { best_gain_d = delta; best_i = i; best_j = j; }
            } else if (config.distance_type == DistanceType::Int32) {
                int delta = get_distance_int32(config, context, i, i_next) + get_distance_int32(config, context, j, j_next)
                          - get_distance_int32(config, context, i, j) - get_distance_int32(config, context, i_next, j_next);
                if (delta > best_gain_i32) { best_gain_i32 = delta; best_i = i; best_j = j; }
            } else {
                long long delta = get_distance_int64(config, context, i, i_next) + get_distance_int64(config, context, j, j_next)
                                - get_distance_int64(config, context, i, j) - get_distance_int64(config, context, i_next, j_next);
                if (delta > best_gain_ll) { best_gain_ll = delta; best_i = i; best_j = j; }
            }
        }
    }

    if (best_i != -1) {
        return apply_2_opt_move(config, context, best_i, best_j);
    }
    return false;
}

int local_2_opt_search(const Config& config, Context& context) {
    // 2-opt to convergence, then Or-opt(1) relocations; loop until both stall
    int improved_times = 0;

    auto try_or_opt_once = [&](void) -> bool {
        for (int u = 0; u < config.cities_number; ++u) {
            int p = context.path[u].prev;
            int n = context.path[u].next;
            if (p == null || n == null) continue;

            for (int kk = 0; kk < config.candidates_number; ++kk) {
                int j = context.candidates[u * config.candidates_number + kk];
                if (j == u || j == p || j == n) continue;
                int jn = context.path[j].next;
                if (jn == u) continue;

                // mutual-KNN gate between (u,j)
                bool mutual = false;
                for (int t = 0; t < config.candidates_number; ++t) {
                    if (context.candidates[j * config.candidates_number + t] == u) { mutual = true; break; }
                }
                if (!mutual) continue;

                bool apply_move = false;
                double w_increase = 0.0;

                if (config.distance_type == DistanceType::Double) {
                    double L = context.path_distance_double;
                    double delta = get_distance_double(config, context, p, u) + get_distance_double(config, context, u, n) + get_distance_double(config, context, j, jn)
                                 - (get_distance_double(config, context, p, n) + get_distance_double(config, context, j, u) + get_distance_double(config, context, u, jn));
                    if (delta > 0.0) {
                        apply_move = true;
                        w_increase = config.weight_delta_coefficient * std::expm1(delta / L);
                        context.path_distance_double -= delta;
                    }
                } else if (config.distance_type == DistanceType::Int32) {
                    double L = static_cast<double>(context.path_distance_int32);
                    int delta = get_distance_int32(config, context, p, u) + get_distance_int32(config, context, u, n) + get_distance_int32(config, context, j, jn)
                              - (get_distance_int32(config, context, p, n) + get_distance_int32(config, context, j, u) + get_distance_int32(config, context, u, jn));
                    if (delta > 0) {
                        apply_move = true;
                        w_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(delta) / L);
                        context.path_distance_int32 -= delta;
                    }
                } else {
                    double L = static_cast<double>(context.path_distance_int64);
                    long long delta = get_distance_int64(config, context, p, u) + get_distance_int64(config, context, u, n) + get_distance_int64(config, context, j, jn)
                                    - (get_distance_int64(config, context, p, n) + get_distance_int64(config, context, j, u) + get_distance_int64(config, context, u, jn));
                    if (delta > 0) {
                        apply_move = true;
                        w_increase = config.weight_delta_coefficient * std::expm1(static_cast<double>(delta) / L);
                        context.path_distance_int64 -= delta;
                    }
                }

                if (apply_move) {
                    // detach u from (p,u,n)
                    context.path[p].next = n;
                    context.path[n].prev = p;
                    // insert u between (j, jn)
                    context.path[j].next = u;
                    context.path[u].prev = j;
                    context.path[u].next = jn;
                    context.path[jn].prev = u;

                    // reinforce new edges
                    update_weight_undirected(config, context, p, n, w_increase);
                    update_weight_undirected(config, context, j, u, w_increase);
                    update_weight_undirected(config, context, u, jn, w_increase);

                    return true;
                }
            }
        }
        return false;
    };

    bool changed;
    do {
        changed = false;
        while (improve_by_2_opt_move(config, context)) { ++improved_times; changed = true; }
        if (try_or_opt_once()) { ++improved_times; changed = true; }
    } while (changed);

    return improved_times;
}
