#pragma once


// * The following functions are used to randomly generate an initial solution.
// * Starting from an arbitrarily chosen city, it iteratively selects a random city until forming a complete TSP tour.


int select_random_city(const Config& config, Context& context) {
	int random_start = get_random_int_by_module(config.cities_number);

	for (int i = 0; i < config.cities_number; ++i) {
		int city = (random_start + i) % config.cities_number;

		if (!context.is_city_selected[city]) {
			return city;
		}
	}

	return null;
}

void generate_random_solution(const Config& config, Context& context) {
    for (int i = 0; i < config.cities_number; ++i) {
        context.solution[i] = null;
        context.is_city_selected[i] = false;
    }

    // Start from a random city…
    int start = select_random_city(config, context);
    context.solution[0] = start;
    context.is_city_selected[start] = true;

    int current = start;

    // …then greedily pick the nearest *unselected* candidate (by squared distance).
    for (int pos = 1; pos < config.cities_number; ++pos) {
        int next_city = null;
        double best_d2 = inf_double;

        // Prefer KNN candidate set for speed.
        for (int j = 0; j < config.candidates_number; ++j) {
            int cand = context.candidates[current * config.candidates_number + j];
            if (cand == current || context.is_city_selected[cand]) { continue; }
            double dx = context.coordinates_double_x[current] - context.coordinates_double_x[cand];
            double dy = context.coordinates_double_y[current] - context.coordinates_double_y[cand];
            double d2 = dx * dx + dy * dy;
            if (d2 < best_d2) { best_d2 = d2; next_city = cand; }
        }

        // Fallback: global nearest if all KNN are already taken (rare late stage).
        if (next_city == null) {
            for (int cand = 0; cand < config.cities_number; ++cand) {
                if (cand == current || context.is_city_selected[cand]) { continue; }
                double dx = context.coordinates_double_x[current] - context.coordinates_double_x[cand];
                double dy = context.coordinates_double_y[current] - context.coordinates_double_y[cand];
                double d2 = dx * dx + dy * dy;
                if (d2 < best_d2) { best_d2 = d2; next_city = cand; }
            }
        }

        // Commit choice
        context.solution[pos] = next_city;
        context.is_city_selected[next_city] = true;
        current = next_city;
    }
}
