    Context context(config);

    // reading input data
    std::cout << "Reading input data...\n";
    read_input_data(config, context);

    // solving
    std::cout << "Solving...\n";
    solve(config, context);

    // printing the solution
    std::ofstream output_file(config.output_path);

    std::cout << "Final solution:\n";
    for (int i = 0; i < config.cities_number; ++i) {
        std::cout << context.solution[i] << ' ';
        output_file << context.solution[i] << ' ';
    }
    // Compute true Euclidean length from coordinates to avoid relying on distance_double contents
    double final_total = 0.0;
    for (int c = 0; c < config.cities_number; ++c) {
        int nxt = context.path[c].next;
        double dx = context.coordinates_double_x[c] - context.coordinates_double_x[nxt];
        double dy = context.coordinates_double_y[c] - context.coordinates_double_y[nxt];
        final_total += std::sqrt(dx * dx + dy * dy);
    }
    std::cout << "\n\nFinal solution score: " << final_total << '\n';

    return 0;
}
