#pragma once


bool apply_2_opt_move(const Config& config, Context& context, int i, int j) {
    if (is_cities_same_or_adjacent(config, context, i, j)) { return false; }

    int i_next = context.path[i].next;
    int j_next = context.path[j].next;

    ++context.chosen_times[i * config.cities_number + j];
    ++context.chosen_times[j * config.cities_number + i];
    ++context.chosen_times[i_next * config.cities_number + j_next];
    ++context.chosen_times[j_next * config.cities_number + i_next];

    ++context.total_simulations;

    // --- Accept by TRUE Euclidean gain (removes int-quantization plateaus) ---
    const double d_ii = get_distance_double(config, context, i, i_next);
    const double d_jj = get_distance_double(config, context, j, j_next);
    const double d_ij = get_distance_double(config, context, i, j);
    const double d_in_jn = get_distance_double(config, context, i_next, j_next);
    const double delta_true = (d_ii + d_jj) - (d_ij + d_in_jn); // gain > 0 ⇒ improvement

    if (delta_true <= 0.0) return false;

    // applying 2-opt (reverse subpath and reconnect)
    reverse_sub_path(context, i_next, j);

    context.path[i].next = j;
    context.path[i_next].next = j_next;
    context.path[j].prev = i;
    context.path[j_next].prev = i_next;

    // update true distance (always maintained)
    context.path_distance_double -= delta_true;

    // update typed accumulators best-effort (not used for selection anymore)
    if (config.distance_type == DistanceType::Int32) {
        const int gain_i =
            get_distance_int32(config, context, i, i_next) +
            get_distance_int32(config, context, j, j_next) -
            get_distance_int32(config, context, i, j) -
            get_distance_int32(config, context, i_next, j_next);
        if (gain_i > 0) context.path_distance_int32 -= gain_i;
    } else if (config.distance_type == DistanceType::Int64) {
        const long long gain_ll =
            get_distance_int64(config, context, i, i_next) +
            get_distance_int64(config, context, j, j_next) -
            get_distance_int64(config, context, i, j) -
            get_distance_int64(config, context, i_next, j_next);
        if (gain_ll > 0) context.path_distance_int64 -= gain_ll;
    }

    // weight update uses stable expm1
    const double denom = (context.path_distance_double > 0.0 && std::isfinite(context.path_distance_double))
                         ? context.path_distance_double : (d_ii + d_jj) * config.cities_number;
    const double weight_increase = config.weight_delta_coefficient * std::expm1(delta_true / denom);
    update_weight_undirected(config, context, i, j, weight_increase);
    update_weight_undirected(config, context, i_next, j_next, weight_increase);

    return true;
}


bool improve_by_2_opt_move(const Config& config, Context& context) {
    // Steepest 2-opt over union neighborhood: KNN(i) ∪ KNN(i_next), with mutual-KNN gating
    double best_gain = 0.0;
    int best_i = -1, best_j = -1;

    for (int i = 0; i < config.cities_number; ++i) {
        int i_next = context.path[i].next;

        // scan KNN(i)
        for (int jidx = 0; jidx < config.candidates_number; ++jidx) {
            int j = context.candidates[i * config.candidates_number + jidx];
            if (is_cities_same_or_adjacent(config, context, i, j)) continue;

            bool mutual = false;
            for (int k = 0; k < config.candidates_number; ++k) {
                if (context.candidates[j * config.candidates_number + k] == i) { mutual = true; break; }
            }
            if (!mutual) continue;

            int j_next = context.path[j].next;

            const double d_ii = get_distance_double(config, context, i, i_next);
            const double d_jj = get_distance_double(config, context, j, j_next);
            const double d_ij = get_distance_double(config, context, i, j);
            const double d_in_jn = get_distance_double(config, context, i_next, j_next);
            const double gain = (d_ii + d_jj) - (d_ij + d_in_jn);

            if (gain > best_gain) { best_gain = gain; best_i = i; best_j = j; }
        }

        // scan KNN(i_next) to expand search coverage
        for (int jidx = 0; jidx < config.candidates_number; ++jidx) {
            int j = context.candidates[i_next * config.candidates_number + jidx];
            if (is_cities_same_or_adjacent(config, context, i, j)) continue;

            bool mutual = false;
            for (int k = 0; k < config.candidates_number; ++k) {
                if (context.candidates[j * config.candidates_number + k] == i) { mutual = true; break; }
            }
            if (!mutual) continue;

            int j_next = context.path[j].next;

            const double d_ii = get_distance_double(config, context, i, i_next);
            const double d_jj = get_distance_double(config, context, j, j_next);
            const double d_ij = get_distance_double(config, context, i, j);
            const double d_in_jn = get_distance_double(config, context, i_next, j_next);
            const double gain = (d_ii + d_jj) - (d_ij + d_in_jn);

            if (gain > best_gain) { best_gain = gain; best_i = i; best_j = j; }
        }
    }

    if (best_gain > 0.0) {
        return apply_2_opt_move(config, context, best_i, best_j);
    }
    return false;
}

int local_2_opt_search(const Config& config, Context& context) {
    int improved_times = 0;
    while (improve_by_2_opt_move(config, context)) { ++improved_times; }
    return improved_times;
}

// ------------------------------------------------------------
// Or-opt(1) relocate: move single node u from (p,u,n) to between (j,j_next)
// Gain (true Euclidean):
//   Δ = [d(p,u)+d(u,n)+d(j,j_next)] - [d(p,n)+d(j,u)+d(u,j_next)]
// ------------------------------------------------------------
static inline bool apply_or_opt1_move(const Config& config, Context& context, int u, int j) {
    int p = context.path[u].prev;
    int n = context.path[u].next;
    if (p == j || n == j || u == j) return false;
    int j_next = context.path[j].next;
    if (j_next == u || j_next == p) return false;

    const double gain_true =
        get_distance_double(config, context, p, u) + get_distance_double(config, context, u, n) + get_distance_double(config, context, j, j_next)
        - (get_distance_double(config, context, p, n) + get_distance_double(config, context, j, u) + get_distance_double(config, context, u, j_next));

    if (gain_true <= 0.0) return false;

    // detach u
    context.path[p].next = n;
    context.path[n].prev = p;

    // insert between j and j_next
    context.path[j].next = u;
    context.path[u].prev = j;
    context.path[u].next = j_next;
    context.path[j_next].prev = u;

    context.path_distance_double -= gain_true;

    if (config.distance_type == DistanceType::Int32) {
        int gain_i =
            get_distance_int32(config, context, p, u) + get_distance_int32(config, context, u, n) + get_distance_int32(config, context, j, j_next)
            - (get_distance_int32(config, context, p, n) + get_distance_int32(config, context, j, u) + get_distance_int32(config, context, u, j_next));
        if (gain_i > 0) context.path_distance_int32 -= gain_i;
    } else if (config.distance_type == DistanceType::Int64) {
        long long gain_ll =
            get_distance_int64(config, context, p, u) + get_distance_int64(config, context, u, n) + get_distance_int64(config, context, j, j_next)
            - (get_distance_int64(config, context, p, n) + get_distance_int64(config, context, j, u) + get_distance_int64(config, context, u, j_next));
        if (gain_ll > 0) context.path_distance_int64 -= gain_ll;
    }

    return true;
}

static inline bool improve_by_or_opt1(const Config& config, Context& context) {
    double best_gain = 0.0; int best_u = -1, best_j = -1;

    for (int u = 0; u < config.cities_number; ++u) {
        int p = context.path[u].prev, n = context.path[u].next;
        for (int k = 0; k < config.candidates_number; ++k) {
            int j = context.candidates[u * config.candidates_number + k];

            if (p == j || n == j || u == j) continue;
            int j_next = context.path[j].next;
            if (j_next == u || j_next == p) continue;

            const double gain_true =
                get_distance_double(config, context, p, u) + get_distance_double(config, context, u, n) + get_distance_double(config, context, j, j_next)
                - (get_distance_double(config, context, p, n) + get_distance_double(config, context, j, u) + get_distance_double(config, context, u, j_next));

            if (gain_true > best_gain) { best_gain = gain_true; best_u = u; best_j = j; }
        }
    }
    if (best_gain > 0.0) return apply_or_opt1_move(config, context, best_u, best_j);
    return false;
}

int local_or_opt_search(const Config& config, Context& context) {
    int improved_times = 0;
    while (improve_by_or_opt1(config, context)) { ++improved_times; }
    return improved_times;
}
