#pragma once

// Cheap ILS: two unconditional 2-opt "kicks" to escape local minima when 2-opt+Or-opt stall.
// We reuse the same topology update as in apply_2_opt_move but skip the gain check and recompute L.

static inline void force_2opt_kick(const Config& config, Context& context, int i, int j) {
    if (i == j) return;
    if (context.path[i].next == j || context.path[j].next == i) return;

    int i_next = context.path[i].next;
    int j_next = context.path[j].next;

    // reverse the subpath (i_next..j)
    reverse_sub_path(context, i_next, j);

    // reconnect endpoints like 2-opt
    context.path[i].next = j;
    context.path[j].prev = i;
    context.path[i_next].next = j_next;
    context.path[j_next].prev = i_next;

    // exact recompute after kick (rare, O(n))
    context.path_distance_double = calc_total_distance_double(config, context);
    if (config.distance_type == DistanceType::Int32) {
        context.path_distance_int32 = calc_total_distance_int32(config, context);
    } else if (config.distance_type == DistanceType::Int64) {
        context.path_distance_int64 = calc_total_distance_int64(config, context);
    }
}

static inline void random_2edge_kick(const Config& config, Context& context) {
    int n = config.cities_number;
    int i = rand() % n;
    int j = rand() % n;
    // ensure non-adjacent picks
    int guard = 0;
    while ((i == j) || context.path[i].next == j || context.path[j].next == i) {
        j = rand() % n;
        if (++guard > 5 * n) break;
    }
    force_2opt_kick(config, context, i, j);
}
