#pragma once


// * The following functions are used to randomly generate an initial solution.
// * Starting from an arbitrarily chosen city, it iteratively selects a random city until forming a complete TSP tour.


int select_random_city(const Config& config, Context& context) {
	int random_start = get_random_int_by_module(config.cities_number);

	for (int i = 0; i < config.cities_number; ++i) {
		int city = (random_start + i) % config.cities_number;

		if (!context.is_city_selected[city]) {
			return city;
		}
	}

	return null;
}

void generate_random_solution(const Config& config, Context& context) {
	// GRASP-style KNN constructor with multi-start and distance-biased sampling.
	// Builds several candidate tours and keeps the best (shortest) one.
	// This yields far better starting tours than pure random chains,
	// cutting downstream 2-opt/k-opt time and improving final quality.

	const int n = config.cities_number;
	const int starts = 8;                 // more multi-starts; still cheap at n≈200
	const int topL  = std::min(8, config.candidates_number);  // RCL cap
	const double eps = 1e-12;

	for (int i = 0; i < n; ++i) {
		context.solution[i] = null;
		context.is_city_selected[i] = false;
	}

	double best_len = std::numeric_limits<double>::infinity();
	std::vector<int> best_sol(n);

	for (int s = 0; s < starts; ++s) {
		std::vector<int> sol(n, null);
		std::vector<char> used(n, 0);

		int current = select_random_city(config, context);
		sol[0] = current;
		used[current] = 1;
		double len = 0.0;

		for (int pos = 1; pos < n; ++pos) {
			// Build restricted candidate list (up to topL nearest unvisited from KNN)
			int rcl_idx[8];
			double rcl_dist[8];
			int rsz = 0;

			for (int t = 0; t < config.candidates_number; ++t) {
				int cand = context.candidates[current * config.candidates_number + t];
				if (cand == current || used[cand]) continue;

				double d = get_distance_double(config, context, current, cand);

				// keep a sorted list of size<=topL by distance
				if (rsz < topL) {
					rcl_idx[rsz] = cand;
					rcl_dist[rsz] = d;
					++rsz;

					for (int k = rsz - 1; k > 0; --k) {
						if (rcl_dist[k] < rcl_dist[k - 1]) {
							std::swap(rcl_dist[k], rcl_dist[k - 1]);
							std::swap(rcl_idx[k], rcl_idx[k - 1]);
						}
					}
				} else if (d < rcl_dist[rsz - 1]) {
					rcl_dist[rsz - 1] = d;
					rcl_idx[rsz - 1] = cand;
					for (int k = rsz - 1; k > 0; --k) {
						if (rcl_dist[k] < rcl_dist[k - 1]) {
							std::swap(rcl_dist[k], rcl_dist[k - 1]);
							std::swap(rcl_idx[k], rcl_idx[k - 1]);
						}
					}
				}
			}

			int next_city = null;
			if (rsz > 0) {
				// Distance-biased roulette: p ∝ 1 / d
				double wsum = 0.0;
				for (int k = 0; k < rsz; ++k) wsum += 1.0 / (rcl_dist[k] + eps);
				double r = (static_cast<double>(rand()) / RAND_MAX) * wsum;
				for (int k = 0; k < rsz; ++k) {
					r -= 1.0 / (rcl_dist[k] + eps);
					if (r <= 0.0) { next_city = rcl_idx[k]; break; }
				}
				if (next_city == null) next_city = rcl_idx[rsz - 1]; // numeric fallback
			} else {
				// Fallback: global nearest unvisited
				double best_d = std::numeric_limits<double>::infinity();
				for (int cand = 0; cand < n; ++cand) {
					if (used[cand]) continue;
					double d = get_distance_double(config, context, current, cand);
					if (d < best_d) { best_d = d; next_city = cand; }
				}
			}

			len += get_distance_double(config, context, current, next_city);
			current = next_city;
			sol[pos] = current;
			used[current] = 1;
		}
		// close the tour
		len += get_distance_double(config, context, sol[n - 1], sol[0]);

		if (len < best_len) {
			best_len = len;
			best_sol = sol;
		}
	}

	// output the best found start
	for (int i = 0; i < n; ++i) context.solution[i] = best_sol[i];
	for (int i = 0; i < n; ++i) context.is_city_selected[i] = false; // clean flags
}
